execution.script:
podTemplate(label: 'mono-pod', containers: [
    containerTemplate(name: 'docker', image: 'danghung/trainingdocker:nx5', command: 'cat', ttyEnabled: true),
    containerTemplate(name: 'kubectl', image: 'public.ecr.aws/smartlog/roffe/kubectl:v1.13.2', command: 'cat', ttyEnabled: true),
    containerTemplate(name: 'awscli', image: 'public.ecr.aws/smartlog/atlassian/pipelines-awscli:1.18.190', command: 'cat', ttyEnabled: true),
  ],
  volumes: [
    hostPathVolume(mountPath: '/var/run/docker.sock', hostPath: '/var/run/docker.sock'),
    persistentVolumeClaim(claimName: 'pvc-monorepo-cache', mountPath: '/usr/local/share')
  ]) {
    node('mono-pod') {
      commitMessage = ""
      affectedApp = ""
      try {
        stage('Get source code') {
          echo 'Getting source code...'
          checkout([
            $class: 'GitSCM',
            branches: [[name: '*/main']],
            extensions: scm.extensions,
            userRemoteConfigs: [[
              credentialsId: scm.userRemoteConfigs[0].credentialsId,
              name: 'origin',
              refspec: '+refs/heads/*:refs/remotes/origin/*',
              url: scm.userRemoteConfigs[0].url
            ]],
            doGenerateSubmoduleConfigurations: false
          ])
          checkout scm
          String[] parts = sh(returnStdout: true, script: 'git log -1 --pretty=%B').trim().split('\n')
          commitMessage = parts[0]
        }
        if (env.BRANCH_NAME == 'main') {
          echo "Process build for ${env.BRANCH_NAME} branch"
          stage('Build docker') {
            container('awscli') {
              DOCKER_LOGIN = sh (
                script: 'aws ecr get-login --no-include-email --region ap-southeast-1',
                returnStdout: true
              ).trim()
            }
            container('docker') {
              REGISTRY_URL="744004065806.dkr.ecr.ap-southeast-1.amazonaws.com/smartlog-app-monorepo"
            //   npm run nx affected:build --base= origin/main~1 --head= origin/main
            // npm run nx run smartlog-admin-angular:docker
              sh """
              npm i
              docker run --privileged --rm tonistiigi/binfmt --install all
              docker buildx create --use --driver-opt network=host --buildkitd-flags '--allow-insecure-entitlement network.host'
              ${DOCKER_LOGIN}
              yarn nx affected --target=docker --base=remotes/origin/main

              """
            }
          }
          // stage('Deploy k8s') {
          //   container('kubectl') {
          //     sh """

          //     """
          //   }
          // }
          currentBuild.result = 'SUCCESS'
        } else if (env.BRANCH_NAME == 'dev') {
          echo "Process build for ${env.BRANCH_NAME} branch"
          stage('Docker Login') {
            container('awscli') {
              DOCKER_LOGIN = sh (
                script: 'aws ecr get-login --no-include-email --region ap-southeast-1',
                returnStdout: true
              ).trim()
            }
          }
          container('docker') {
              REGISTRY_URL="744004065806.dkr.ecr.ap-southeast-1.amazonaws.com/smartlog-app-monorepo"
              // yarn nx affected:build --base= origin/main~1 --head= origin/main
            // yarn nx build smartlog-admin-angular
            //   yarn nx run smartlog-admin-angular:docker
              stage('yarn') {
                sh """
                mkdir -p /usr/local/share/node_modules
                ln -s  /usr/local/share/node_modules node_modules
                mkdir -p /usr/local/share/Nuget
                mkdir -p ~/.local/share
                ln -s  /usr/local/share/Nuget ~/.local/share/NuGet
                yarn
                """
              }
              // docker run --privileged --rm tonistiigi/binfmt --install all
              stage('Prepare build') {
                sh """
                    docker buildx create --use --driver-opt network=host --buildkitd-flags '--allow-insecure-entitlement network.host'
                    ${DOCKER_LOGIN}
                    git config --global --add safe.directory /home/jenkins/agent/workspace/SmartlogApp_${env.BRANCH_NAME}
                    mkdir -p /usr/local/share/.angular
                    ln -s /usr/local/share/.angular .angular
                """
              }
              stage('Build app') {
                sh """
                    echo NX_WORKSPACE_ROOT_PATH=/home/jenkins/agent/workspace/SmartlogApp_${env.BRANCH_NAME} >> .env
                """
                affectedApp = sh (  script: "node_modules/.bin/nx affected:apps '--base=remotes/origin/dev~1' --head=HEAD --plain", returnStdout: true  ).trim()
                sh """
                    yarn nx affected:build --base=remotes/origin/dev~1 --head=HEAD --skip-nx-cache --runner=jenkins
                """
                sh "echo affectedApp: ${affectedApp}"
              }
              stage('Build docker') {
                sh 'yarn nx affected --target=docker --base=remotes/origin/dev~1 --head=HEAD --parallel=1 --runner=jenkins'
              }
              stage('Deploy') {
                sh 'yarn nx affected --target=deploystage --base=remotes/origin/dev~1 --head=HEAD'
              }
            }


          // stage('Deploy k8s') {
          //   container('kubectl') {

          //   }
          // }
          currentBuild.result = 'SUCCESS'
        } else if (env.BRANCH_NAME == 'production') {
          echo "Process build for ${env.BRANCH_NAME} branch"
          stage('Docker Login') {
            container('awscli') {
              DOCKER_LOGIN = sh (
                script: 'aws ecr get-login --no-include-email --region ap-southeast-1',
                returnStdout: true
              ).trim()
            }
          }
          container('docker') {
              REGISTRY_URL="744004065806.dkr.ecr.ap-southeast-1.amazonaws.com/smartlog-app-monorepo"
              // yarn nx affected:build --base= origin/main~1 --head= origin/main
            // yarn nx build smartlog-admin-angular
            //   yarn nx run smartlog-admin-angular:docker
              stage('yarn') {
                sh """
                mkdir -p /usr/local/share/node_modules
                ln -s  /usr/local/share/node_modules node_modules
                mkdir -p /usr/local/share/Nuget
                mkdir -p ~/.local/share
                ln -s  /usr/local/share/Nuget ~/.local/share/NuGet
                yarn
                """
              }
              // docker run --privileged --rm tonistiigi/binfmt --install all
              stage('Prepare build') {
                sh """
                    docker buildx create --use --driver-opt network=host --buildkitd-flags '--allow-insecure-entitlement network.host'
                    ${DOCKER_LOGIN}
                    git config --global --add safe.directory /home/jenkins/agent/workspace/SmartlogApp_${env.BRANCH_NAME}
                    mkdir -p /usr/local/share/.angular
                    ln -s /usr/local/share/.angular .angular
                """
              }
              stage('Build app') {
                sh """
                    echo NX_WORKSPACE_ROOT_PATH=/home/jenkins/agent/workspace/SmartlogApp_${env.BRANCH_NAME} >> .env
                """
                affectedApp = sh (  script: "node_modules/.bin/nx affected:apps '--base=remotes/origin/production~1' --head=HEAD --plain", returnStdout: true  ).trim()
                sh """
                    yarn nx affected:build --base=remotes/origin/production~1 --head=HEAD --skip-nx-cache --runner=jenkins
                """
                sh "echo affectedApp: ${affectedApp}"
              }
              stage('Build docker') {
                sh 'yarn nx affected --target=dockerprod --base=remotes/origin/production~1 --head=HEAD --parallel=1 --runner=jenkins'
              }
              stage('Deploy') {
                sh 'yarn nx affected --target=deployprod --base=remotes/origin/production~1 --head=HEAD'
              }
            }


          // stage('Deploy k8s') {
          //   container('kubectl') {

          //   }
          // }
          currentBuild.result = 'SUCCESS'
          } else {
          echo "Khong phai branch (production|dev) nen ko lam gi"
          error('Failed to build')
        }

      } catch (exc) {
        currentBuild.result = 'FAILURE'
        echo 'I failed'
        echo exc.getMessage()
      }
      finally {
        echo 'One way or another, I have finished'
        // deleteDir() /* clean up our workspace */
        if (currentBuild.result == 'SUCCESS') {
          echo 'Build successful'
          slackSend channel: 'smartlog_admin_workspace_notification',
            color: 'good',
            message: "The pipeline ${currentBuild.fullDisplayName} [${commitMessage}] completed successfully.\nBuild app: ${affectedApp}"
        } else if (currentBuild.result == 'FAILURE') {
          echo 'I failed :('
          slackSend channel: 'smartlog_admin_workspace_notification',
            color: '#c40e0e',
            message: "Attention @here ${env.JOB_NAME} [${commitMessage}] #${env.BUILD_NUMBER} has failed. ${env.BUILD_URL}/console\nBuild app:${affectedApp}\n  "
        }
      }
    }
  }
