entry.string:
/* groovylint-disable LineLength */
def awscli(running = true) {
    if (running) {
        container('awscli') {
            DOCKER_LOGIN = sh (
                script: 'aws ecr get-login --no-include-email --region ap-southeast-1',
                returnStdout: true
            ).trim()
        }
    }
}

def generateVersion(running = true) {
    if (running) {
        container('gitversion') {
            SEM_VER = sh (
                script: '/tools/dotnet-gitversion . /showvariable SemVer',
                returnStdout: true
            ).trim()
            echo "SemVer: ${SEM_VER}"
        }
    }
}

def loadS3File(source, dest, running = true) {
    if (running) {
        container('awscli') {
            sh """aws s3 cp ${source} ${dest}"""
        }
    }
}

def buildDocker(running = true) {
    if (running) {
        container('docker') {
            REGISTRY_URL = """744004065806.dkr.ecr.ap-southeast-1.amazonaws.com/${prefixPod}:${tagEnv}"""
            sh """
                ls -la
                docker build -f ${repoPath}Dockerfile.${tagEnv} --build-arg version=${SEM_VER} --network=host -t ${REGISTRY_URL} --pull=true .
                ${DOCKER_LOGIN}
                docker push ${REGISTRY_URL}
            """
        }
        scr = "aws ecr describe-images --registry-id 744004065806 --repository-name ${prefixPod} --image-ids imageTag=${tagEnv} --query 'sort(imageDetails[].imageDigest)' --output text --region ap-southeast-1"
        container('awscli') { 
            def sha256 = sh(
                script: scr,
                returnStdout: true
            ).trim()
            SHA256 = sha256
        }
        currentTimestamp = sh(returnStdout: true, script: 'date +%s').trim()
        REGISTRY_URL="744004065806.dkr.ecr.ap-southeast-1.amazonaws.com/${prefixPod}"
        script {
            withCredentials([usernamePassword(credentialsId: 'cicd-bitbucket', passwordVariable: 'BITBUCKET_PASSWORD', usernameVariable: 'BITBUCKET_USERNAME')]) {
            //def encodedPassword = URLEncoder.encode("$GIT_PASSWORD",'UTF-8')
            sh """
            git config --global user.email jenkins@gosmartlog.com
            git config --global user.name jenkins
            rm -rf devops-deployment
            git clone https://${BITBUCKET_USERNAME}:${BITBUCKET_PASSWORD}@bitbucket.org/smartlogtms/devops-deployment.git
            cd devops-deployment
            sed -i 's+${REGISTRY_URL}.*+${REGISTRY_URL}@${SHA256}+g' ./smartlog-ecosystem/${env.BRANCH_NAME}/${gitFolder}/.argocd-source.yaml
            git add ./smartlog-ecosystem/${env.BRANCH_NAME}/${gitFolder}/.argocd-source.yaml
            for file in ./smartlog-ecosystem/${env.BRANCH_NAME}/${gitFolder}/deploy*.yaml; do sed -i "s/\\(date:\\s*\\)'[^']*'/\\1'${currentTimestamp}'/" "\$file";git add "\$file";done
            git commit -m 'Done by Jenkins Job changemanifest: ${prefixPod}:${env.BUILD_NUMBER}'
            git push https://${BITBUCKET_USERNAME}:${BITBUCKET_PASSWORD}@bitbucket.org/smartlogtms/devops-deployment.git HEAD:refs/heads/master
            """
            }
        }
    }
}

def deployToK8s(running = true) {
    if (running) {
        container('kubectl') {
            sh """
                kubectl apply -f ${repoPath}deploy.${tagEnv}.yml
                kubectl patch deployment ${prefixPod}-${tagEnv} -p "{\\\"spec\\\":{\\\"template\\\":{\\\"metadata\\\":{\\\"labels\\\":{\\\"date\\\":\\\"`date +'%s'`\\\"}}}}}" -n ${namespace}
                kubectl rollout status deployment ${prefixPod}-${tagEnv} -n ${namespace}
            """
        }
    }
}


return this

