execution.script:
podTemplate(label: 'pod-with-gitversion', containers: [
    containerTemplate(name: 'docker', image: 'public.ecr.aws/smartlog/docker:19.03.8', ttyEnabled: true, command: 'cat'),
    containerTemplate(name: 'kubectl', image: 'public.ecr.aws/smartlog/roffe/kubectl:v1.13.2', command: 'cat', ttyEnabled: true),
    containerTemplate(name: 'awscli', image: 'public.ecr.aws/smartlog/atlassian/pipelines-awscli:1.18.190', command: 'cat', ttyEnabled: true),
    containerTemplate(name: 'gitversion', image: 'gittools/gitversion:5.11.1-alpine.3.13-6.0', command: 'cat', ttyEnabled: true),
  ],
  volumes: [
    hostPathVolume(mountPath: '/var/run/docker.sock', hostPath: '/var/run/docker.sock'),
  ]) {
  node('pod-with-gitversion') {
    isDeploy = false

    isBuildAuthFe = false
    isBuildAuthWorker = false
    isBuildSocketService = false
    isBuildNotificationService = false
    isBuildLocationService = false

    isBuildAuditLogService = false
    isBuildAdministrationService = false
    isBuildAppGateway = false
    isBuildSocialService = false
    isBuildCosService = false

    isBuildTransportExchange = false
    successBuild = 0
    totalBuild = 0

    commitMessage = ''
    try {
      SEM_VER = '0.0.0'
      stage('Get source code') {
        echo 'Getting source code...'
        checkout([
          $class: 'GitSCM',
          branches: [[name: '*/dev']],
          extensions: scm.extensions,
          userRemoteConfigs: [[
            credentialsId: scm.userRemoteConfigs[0].credentialsId,
            name: 'origin',
            refspec: '+refs/heads/*:refs/remotes/origin/*',
            url: scm.userRemoteConfigs[0].url
          ]],
          doGenerateSubmoduleConfigurations: false
        ])

        checkout scm

        String[] parts = sh(returnStdout: true, script: 'git log -1 --pretty=%B').trim().split('\n')
        commitMessage = parts[0]

        result = sh (
          script: "git log -1 --pretty=%B | grep 'build-all'",
          returnStatus: true
        )

        // isBuildAuthFe = (result == 0)
        isBuildAuthWorker = (result == 0)
        isBuildSocketService = (result == 0)
        isBuildNotificationService = (result == 0)
        isBuildLocationService = (result == 0)

        isBuildAuditLogService = (result == 0)
        isBuildAdministrationService = (result == 0)
        isBuildAppGateway = (result == 0)
        isBuildSocialLogService = (result == 0)
        isBuildCosService = (result == 0)

        isBuildTransportExchange = (result == 0)

        if (!isBuildAuthFe) {
          isBuildAuthFe = (sh (
            script: 'git diff --name-status HEAD~1..HEAD | grep modules/IdentityServer/smartlog-auth-fe',
            returnStatus: true
          ) == 0)
        }
        echo "isBuildAuthFe: ${isBuildAuthFe}"

        if (!isBuildAuthWorker) {
          isBuildAuthWorker = (sh (
            script: "git diff --name-status HEAD~1..HEAD | grep 'modules/IdentityServer/smartlog-auth-be/Smartlog.Auth.Worker'",
            returnStatus: true
          ) == 0)
        }
        echo "isBuildAuthWorker: ${isBuildAuthWorker}"

        if (!isBuildSocketService) {
          isBuildSocketService = (sh (
            script: 'git diff --name-status HEAD~1..HEAD | grep src/Services/Smartlog.SocketService',
            returnStatus: true
          ) == 0)
        }
        echo "isBuildSocketService: ${isBuildSocketService}"

        if (!isBuildNotificationService) {
          isBuildNotificationService = (sh (
            script: 'git diff --name-status HEAD~1..HEAD | grep src/Services/Notification',
            returnStatus: true
          ) == 0)
        }
        echo "isBuildNotificationService: ${isBuildNotificationService}"

        if (!isBuildLocationService) {
          isBuildLocationService = (sh (
            script: 'git diff --name-status HEAD~1..HEAD | grep src/Services/LocationService',
            returnStatus: true
          ) == 0)
        }
        echo "isBuildLocationService: ${isBuildLocationService}"

        if (!isBuildAuditLogService) {
          isBuildAuditLogService = (sh (
            script: 'git diff --name-status HEAD~1..HEAD | grep src/Services/Smartlog.AuditLog.API',
            returnStatus: true
          ) == 0)
        }
        echo "isBuildAuditLogService: ${isBuildAuditLogService}"

        if (!isBuildAdministrationService) {
          isBuildAdministrationService = (sh (
            script: 'git diff --name-status HEAD~1..HEAD | grep src/Services/Administration',
            returnStatus: true
          ) == 0)
        }
        echo "isBuildAdministrationService: ${isBuildAdministrationService}"

        if (!isBuildAppGateway) {
          isBuildAppGateway = (sh (
            script: 'git diff --name-status HEAD~1..HEAD | grep src/Gateway',
            returnStatus: true
          ) == 0)
        }
        echo "isBuildAppGateway: ${isBuildAppGateway}"

        if (!isBuildSocialService) {
          isBuildSocialService = (sh (
            script: 'git diff --name-status HEAD~1..HEAD | grep src/Services/SocialService',
            returnStatus: true
          ) == 0)
        }
        echo "isBuildSocialService: ${isBuildSocialService}"

        if (!isBuildCosService) {
          isBuildCosService = (sh (
            script: 'git diff --name-status HEAD~1..HEAD | grep src/Services/COS',
            returnStatus: true
          ) == 0)
        }
        echo "isBuildCosService: ${isBuildCosService}"

        if (!isBuildTransportExchange) {
          isBuildTransportExchange = (sh (
            script: 'git diff --name-status HEAD~1..HEAD | grep src/Services/TransportExchange',
            returnStatus: true
          ) == 0)
        }
        echo "isBuildTransportExchange: ${isBuildTransportExchange}"

        isDeploy = isDeploy || isBuildAuthFe || isBuildAuthWorker || isBuildSocketService || isBuildNotificationService || isBuildLocationService
        isDeploy = isDeploy || isBuildAuditLogService || isBuildAdministrationService || isBuildAppGateway || isBuildSocialService || isBuildCosService
        isDeploy = isDeploy || isBuildTransportExchange
        if (isBuildAuthFe) { totalBuild++ }
        if (isBuildAuthWorker) { totalBuild++ }
        if (isBuildSocketService) { totalBuild++ }
        if (isBuildNotificationService) { totalBuild++ }
        if (isBuildLocationService) { totalBuild++ }

        if (isBuildAuditLogService) { totalBuild++ }
        if (isBuildAdministrationService) { totalBuild++ }
        if (isBuildAppGateway) { totalBuild++ }
        if (isBuildSocialService) { totalBuild++ }
        if (isBuildCosService) { totalBuild++ }

        if (isBuildTransportExchange) { totalBuild++ }
      }

      rootDir = pwd()
      baseDeploy = load "${rootDir}/Jenkins.Base.groovy"
      deploy = load "${rootDir}/Jenkins.Build.groovy"
      deploy.init(baseDeploy)
      baseDeploy.awscli()
      if (env.BRANCH_NAME == 'dev' && isDeploy) {
        tagEnv = 'stage'
        longEnv = 'Staging'
        build()
      } else if (env.BRANCH_NAME == 'production' && isDeploy) {
        tagEnv = 'prod'
        longEnv = 'Production'
        build()
      } else if (!isDeploy) {
        echo 'NO BUILD'
      } else {
        echo 'Khong phai branch production || dev nen ko lam gi ca'
        error('Failed to build')
      }
    } catch (exc) {
      currentBuild.result = 'FAILURE'
      echo 'I failed'
      echo exc.getMessage()
    } finally {
      echo 'One way or another, I have finished'
      try {
        deleteDir() /* clean up our workspace */
      } catch (err) {
        // Do nothing
      }
      if (isDeploy != true) {
        /* Do nothing */
      } else if (currentBuild.result == 'SUCCESS') {
        echo 'Build successful'
        // slackSend channel: 'cma-no-boss',
        //   color: 'good',
        //   message: "The pipeline ${currentBuild.fullDisplayName} completed successfully."
      } else if (currentBuild.result == 'FAILURE') {
        echo 'I failed :('
        slackSend channel: 'cma-no-boss',
            color: '#c40e0e',
            message: "Attention @here ${env.JOB_NAME} #${env.BUILD_NUMBER} [${commitMessage}] has failed."
        }
    }
  }
}

def build() {
  echo "Process build for ${env.BRANCH_NAME} branch"
  // baseDeploy.generateVersion()
  SEM_VER = "1.1"

  deploy.buildAuthFe(isBuildAuthFe)
  if (isBuildAuthFe) {
    successBuild++
    slackSend channel: 'cma-no-boss',
        color: 'good',
        message: "The pipeline ${currentBuild.fullDisplayName}[Auth-Fe] v.${SEM_VER} [${commitMessage}] ${successBuild}/${totalBuild} completed successfully."
  }

  deploy.buildAuthWorker(isBuildAuthWorker)
  if (isBuildAuthWorker) {
    successBuild++
    slackSend channel: 'cma-no-boss',
        color: 'good',
        message: "The pipeline ${currentBuild.fullDisplayName}[Auth-Worker] v.${SEM_VER} [${commitMessage}] ${successBuild}/${totalBuild} completed successfully."
  }

  deploy.buildSocketService(isBuildSocketService)
  if (isBuildSocketService) {
    successBuild++
    slackSend channel: 'cma-no-boss',
        color: 'good',
        message: "The pipeline ${currentBuild.fullDisplayName}[SocketService] v.${SEM_VER} [${commitMessage}] ${successBuild}/${totalBuild} completed successfully."
  }

  deploy.buildNotificationService(isBuildNotificationService)
  if (isBuildNotificationService) {
    successBuild++
    slackSend channel: 'cma-no-boss',
        color: 'good',
        message: "The pipeline ${currentBuild.fullDisplayName}[NotificationService] v.${SEM_VER} [${commitMessage}] ${successBuild}/${totalBuild} completed successfully."
  }

  deploy.buildLocationService(isBuildLocationService)
  if (isBuildLocationService) {
    successBuild++
    slackSend channel: 'cma-no-boss',
        color: 'good',
        message: "The pipeline ${currentBuild.fullDisplayName}[LocationService] v.${SEM_VER} [${commitMessage}] ${successBuild}/${totalBuild} completed successfully."
  }

  deploy.buildAuditLogService(isBuildAuditLogService)
  if (isBuildAuditLogService) {
    successBuild++
    slackSend channel: 'cma-no-boss',
        color: 'good',
        message: "The pipeline ${currentBuild.fullDisplayName}[AuditLogService] v.${SEM_VER} [${commitMessage}] ${successBuild}/${totalBuild} completed successfully."
  }

  deploy.buildAdministrationService(isBuildAdministrationService)
  if (isBuildAdministrationService) {
    successBuild++
    slackSend channel: 'cma-no-boss',
        color: 'good',
        message: "The pipeline ${currentBuild.fullDisplayName}[AdministrationService] v.${SEM_VER} [${commitMessage}] ${successBuild}/${totalBuild} completed successfully."
  }

  deploy.buildAppGateway(isBuildAppGateway)
  if (isBuildAppGateway) {
    successBuild++
    slackSend channel: 'cma-no-boss',
        color: 'good',
        message: "The pipeline ${currentBuild.fullDisplayName}[AppGateway] v.${SEM_VER} [${commitMessage}] ${successBuild}/${totalBuild} completed successfully."
  }

  deploy.buildSocialService(isBuildSocialService)
  if (isBuildSocialService) {
    successBuild++
    slackSend channel: 'cma-no-boss',
        color: 'good',
        message: "The pipeline ${currentBuild.fullDisplayName}[SocialService] v.${SEM_VER} [${commitMessage}] ${successBuild}/${totalBuild} completed successfully."
  }

  deploy.buildCosService(isBuildCosService)
  if (isBuildCosService) {
    successBuild++
    slackSend channel: 'cma-no-boss',
        color: 'good',
        message: "The pipeline ${currentBuild.fullDisplayName}[Cos-Be] v.${SEM_VER} [${commitMessage}] ${successBuild}/${totalBuild} completed successfully."
  }

  deploy.buildTransportExchangeBe(isBuildTransportExchange)
  if (isBuildTransportExchange) {
    successBuild++
    slackSend channel: 'cma-no-boss',
        color: 'good',
        message: "The pipeline ${currentBuild.fullDisplayName}[TransportExchange-Be] v.${SEM_VER} [${commitMessage}] ${successBuild}/${totalBuild} completed successfully."
  }

  currentBuild.result = 'SUCCESS'
}

entry.string:
/* groovylint-disable LineLength */
def awscli(running = true) {
    if (running) {
        container('awscli') {
            DOCKER_LOGIN = sh (
                script: 'aws ecr get-login --no-include-email --region ap-southeast-1',
                returnStdout: true
            ).trim()
        }
    }
}

def generateVersion(running = true) {
    if (running) {
        container('gitversion') {
            SEM_VER = sh (
                script: '/tools/dotnet-gitversion . /showvariable SemVer',
                returnStdout: true
            ).trim()
            echo "SemVer: ${SEM_VER}"
        }
    }
}

def loadS3File(source, dest, running = true) {
    if (running) {
        container('awscli') {
            sh """aws s3 cp ${source} ${dest}"""
        }
    }
}

def buildDocker(running = true) {
    if (running) {
        container('docker') {
            REGISTRY_URL = """744004065806.dkr.ecr.ap-southeast-1.amazonaws.com/${prefixPod}:${tagEnv}"""
            sh """
                ls -la
                docker build -f ${repoPath}Dockerfile.${tagEnv} --build-arg version=${SEM_VER} --network=host -t ${REGISTRY_URL} --pull=true .
                ${DOCKER_LOGIN}
                docker push ${REGISTRY_URL}
            """
        }
    }
}

def deployToK8s(running = true) {
    if (running) {
        container('kubectl') {
            sh """
                kubectl apply -f ${repoPath}deploy.${tagEnv}.yml
                kubectl patch deployment ${prefixPod}-${tagEnv} -p "{\\\"spec\\\":{\\\"template\\\":{\\\"metadata\\\":{\\\"labels\\\":{\\\"date\\\":\\\"`date +'%s'`\\\"}}}}}" -n ${namespace}
                kubectl rollout status deployment ${prefixPod}-${tagEnv} -n ${namespace}
            """
        }
    }
}
return this

