import groovy.transform.Field

@Field private Base = null

def init(base) {
    Base = base
}

def buildAuthFe(running = true) {
    namespace = 'eco-system'
    repoPath = 'modules/IdentityServer/smartlog-auth-fe/'
    prefixPod = 'eco-auth-fe'

    stage("Build Auth Fe") {
        Base.buildDocker(running)
        Base.deployToK8s(running)
    }
}

def buildAuthBe(running = true) {
    namespace = 'eco-system'
    repoPath = 'modules/IdentityServer/smartlog-auth-be/Smartlog.Auth.HttpApi.Host/'
    prefixPod = 'eco-auth-be'

    stage("Build Auth Be") {
        Base.loadS3File("s3://smartlog-build-config/stx/ecosystem/auth/appsettings.${tagEnv}.json", "${repoPath}appsettings.${longEnv}.json", running)
        Base.buildDocker(running)
        Base.deployToK8s(running)
    }
}

def buildAuthApiBe(running = true) {
    namespace = 'eco-system'
    repoPath = 'modules/IdentityServer/smartlog-auth-be/Smartlog.Auth.HttpApi.Host/'
    prefixPod = 'eco-auth-api-be'

    stage("Build Auth Api Be") {
        Base.loadS3File("s3://smartlog-build-config/stx/ecosystem/auth/appsettings.${tagEnv}.json", "${repoPath}appsettings.${longEnv}.json", running)
        Base.buildDocker(running)
        // Base.deployToK8s(running)
        stage("Deploy to k8s ${prefixPod}") {
            if (running) {
                container('kubectl') {
                    sh """
                        kubectl apply -f ${repoPath}deploy-api.${tagEnv}.yml
                        kubectl patch deployment ${prefixPod}-${tagEnv} -p "{\\\"spec\\\":{\\\"template\\\":{\\\"metadata\\\":{\\\"labels\\\":{\\\"date\\\":\\\"`date +'%s'`\\\"}}}}}" -n ${namespace}
                        kubectl rollout status deployment ${prefixPod}-${tagEnv} -n ${namespace}
                    """
                }
            }
        }
    }
}

