execution.script:
podTemplate(label: 'pod-with-gitversion', containers: [
    containerTemplate(name: 'kubectl', image: 'public.ecr.aws/smartlog/roffe/kubectl:v1.13.2', command: 'cat', ttyEnabled: true),
    containerTemplate(name: 'buildkit', image: 'moby/buildkit:v0.10.6', ttyEnabled: true, privileged: true),
    containerTemplate(name: 'awscli', image: 'public.ecr.aws/smartlog/atlassian/pipelines-awscli:1.18.190', command: 'cat', ttyEnabled: true),
  ],
  volumes: [
    hostPathVolume(mountPath: '/var/run/docker.sock', hostPath: '/var/run/docker.sock'),
  ]) {
    node('pod-with-gitversion') {
      SLACK_CHANNEL = 'C02CR8V040K' // stm_sme_build_log
      commitMessage = ""
      try {
        SEM_VER = '0.0.0'
        stage('Get source code') {
          echo 'Getting source code...'
          checkout([
            $class: 'GitSCM',
            branches: [[name: '*/dev']],
            extensions: scm.extensions,
            userRemoteConfigs: [[
              credentialsId: scm.userRemoteConfigs[0].credentialsId,
              name: 'origin',
              refspec: '+refs/heads/*:refs/remotes/origin/*',
              url: scm.userRemoteConfigs[0].url
            ]],
            doGenerateSubmoduleConfigurations: false
          ])
          checkout scm

          String[] parts = sh(returnStdout: true, script: 'git log -1 --pretty=%B').trim().split('\n')
          commitMessage = parts[0]
        }

        rootDir = pwd()
        baseDeploy = load "${rootDir}/Jenkins.Base.groovy"
        baseDeploy.awscli()

        if (env.BRANCH_NAME == 'dev') {
          envName = 'stage'
          account_id = '744004065806'
          deployDeliveree = true
          PROJECT = "stm-sme"
          build()
        } else if (env.BRANCH_NAME == 'qc') {
          envName = 'qc'
          account_id = '744004065806'
          deployDeliveree = true
          PROJECT = "stm-sme"
          build()
        } else if (env.BRANCH_NAME == 'argocd') {
          envName = 'stage'
          account_id = '744004065806'
          deployDeliveree = true
          PROJECT = "stm-sme"
          build()
        } else {
          echo "Khong phai branch (dev|qc) nen ko lam gi ca"
          error('Failed to build')
        }
      } catch (exc) {
        currentBuild.result = 'FAILURE'
        echo 'I failed'
        echo exc.getMessage()
      }
      finally {
        echo 'One way or another, I have finished'
        try {
          deleteDir() /* clean up our workspace */
        } catch(err) {
          // Do nothing
        }
        if (currentBuild.result == 'SUCCESS') {
          echo 'Build successful'
          slackSend channel: SLACK_CHANNEL,
            color: 'good',
            message: "The pipeline ${currentBuild.fullDisplayName} [Nikola-Admin-Fe] v${SEM_VER} [${commitMessage}] completed successfully."
        } else if (currentBuild.result == 'FAILURE') {
          // echo 'I failed :('
          // slackSend channel: SLACK_CHANNEL,
          //   color: '#c40e0e',
          //   message: "Attention @here ${env.JOB_NAME} [Nikola-Admin-Fe] v${SEM_VER} #${env.BUILD_NUMBER} [${commitMessage}] has failed."
        }
      }
    }
  }

def build() {
  echo "Process build for ${env.BRANCH_NAME} branch"
  baseDeploy.generateVersion()
  echo "Version ${SEM_VER}"

  namespace = 'stm'
  repoPath = ''
  prefixPod = 'nikola-admin-fe'

  currentTimestamp = sh(returnStdout: true, script: 'date +%s').trim()
  stage('Build') {
    container('buildkit') {
      REGISTRY_URL="${account_id}.dkr.ecr.ap-southeast-1.amazonaws.com/${prefixPod}"
      sh """
      wget https://amazon-ecr-credential-helper-releases.s3.us-east-2.amazonaws.com/0.6.0/linux-amd64/docker-credential-ecr-login -O /usr/local/bin/docker-credential-ecr-login
      chmod 755 /usr/local/bin/docker-credential-ecr-login
      buildctl build --frontend dockerfile.v0 --local context=. --local dockerfile=./ --opt filename=./${DOCKERFILE} --output 'type=image,name=${REGISTRY_URL}:${envName},push=true'
      """
    }
  }
  stage('Update GIT') {
    script {
            withCredentials([usernamePassword(credentialsId: 'cicd-bitbucket', passwordVariable: 'BITBUCKET_PASSWORD', usernameVariable: 'BITBUCKET_USERNAME')]) {
            //def encodedPassword = URLEncoder.encode("$GIT_PASSWORD",'UTF-8')
            sh """
            git config --global user.email jenkins@gosmartlog.com
            git config --global user.name jenkins
            rm -rf devops-deployment-${PROJECT}
            git clone https://${BITBUCKET_USERNAME}:${BITBUCKET_PASSWORD}@bitbucket.org/smartlogtms/devops-deployment-${PROJECT}.git
            cd devops-deployment-${PROJECT}
            for file in ./nikola-admin-fe/${env.BRANCH_NAME}/${gitFolder}/deploy*.yaml; do sed -i "s/\\(date:\\s*\\)'[^']*'/\\1'${currentTimestamp}'/" "\$file";git add "\$file";done
            """
            if (deployDeliveree) {
                sh """
                cd devops-deployment-${PROJECT}
                for file in ./nikola-admin-fe/${env.BRANCH_NAME}-deliveree/${gitFolder}/deploy*.yaml; do sed -i "s/\\(date:\\s*\\)'[^']*'/\\1'${currentTimestamp}'/" "\$file";git add "\$file";done
                """
            }
            sh """
            cd devops-deployment-${PROJECT}
            git commit -m 'Done by Jenkins Job changemanifest: ${prefixPod}:${env.BUILD_NUMBER}'
            git pull --rebase https://${BITBUCKET_USERNAME}:${BITBUCKET_PASSWORD}@bitbucket.org/smartlogtms/devops-deployment-${PROJECT}.git
            git push https://${BITBUCKET_USERNAME}:${BITBUCKET_PASSWORD}@bitbucket.org/smartlogtms/devops-deployment-${PROJECT}.git HEAD:refs/heads/master
            """
            }
    }  
  }
  currentBuild.result = 'SUCCESS'
}
