execution.script:
podTemplate(label: 'common-pod', containers: [
    containerTemplate(name: 'docker', image: 'public.ecr.aws/smartlog/docker:19.03.8', command: 'cat', ttyEnabled: true),
    containerTemplate(name: 'kubectl', image: 'public.ecr.aws/smartlog/roffe/kubectl:v1.13.2', command: 'cat', ttyEnabled: true),
    containerTemplate(name: 'awscli', image: 'public.ecr.aws/smartlog/atlassian/pipelines-awscli:1.18.190', command: 'cat', ttyEnabled: true),
  ],
  volumes: [
    hostPathVolume(mountPath: '/var/run/docker.sock', hostPath: '/var/run/docker.sock'),
  ]) {
    node('common-pod') {
      commitMessage = ""
      try {
        stage('Get source code') {
          echo 'Getting source code...'
          checkout scm
          String[] parts = sh(returnStdout: true, script: 'git log -1 --pretty=%B').trim().split('\n')
          commitMessage = parts[0]
        }
        if (env.BRANCH_NAME == 'staging') {
          echo "Process build for ${env.BRANCH_NAME} branch"
          stage('Build docker') {
            container('awscli') {
              sh 'aws s3 cp s3://smartlog-build-config/swm/oms/appsettings.stage.json src/OMS.WebApi/appsettings.Staging.json'
              DOCKER_LOGIN = sh (
                script: 'aws ecr get-login --no-include-email --region ap-southeast-1',
                returnStdout: true
              ).trim()
            }
            container('docker') {
              REGISTRY_URL="744004065806.dkr.ecr.ap-southeast-1.amazonaws.com/oms-dotnet-be:stage"
              sh """
              ls -la
              docker build -f Dockerfile.stage --network=host -t ${REGISTRY_URL} --pull=true .
              ${DOCKER_LOGIN}
              docker push ${REGISTRY_URL}
              """
            }
          }
          stage('Deploy to k8s') {
            container('kubectl') {
              sh """
              kubectl apply -f deploy.stage.yml
              kubectl patch deployment oms-dotnet-be-stage -p "{\\\"spec\\\":{\\\"template\\\":{\\\"metadata\\\":{\\\"labels\\\":{\\\"date\\\":\\\"`date +'%s'`\\\"}}}}}" -n oms
              kubectl rollout status deployment oms-dotnet-be-stage -n oms
              """
            }
          }
          currentBuild.result = 'SUCCESS'
        } else if (env.BRANCH_NAME == 'production') {
          echo "Process build for ${env.BRANCH_NAME} branch"
          stage('Build docker') {
            container('awscli') {
              sh 'aws s3 cp s3://smartlog-build-config/swm/oms/appsettings.prod.json src/OMS.WebApi/appsettings.Production.json'
              DOCKER_LOGIN = sh (
                script: 'aws ecr get-login --no-include-email --region ap-southeast-1',
                returnStdout: true
              ).trim()
            }
            container('docker') {
              REGISTRY_URL="744004065806.dkr.ecr.ap-southeast-1.amazonaws.com/oms-dotnet-be:prod"
              sh """
              ls -la
              docker build -f Dockerfile.prod --network=host -t ${REGISTRY_URL} --pull=true .
              ${DOCKER_LOGIN}
              docker push ${REGISTRY_URL}
              """
            }
          }
          stage('Deploy to k8s') {
            container('kubectl') {
              sh """
              kubectl apply -f deploy.prod.yml
              kubectl patch deployment oms-dotnet-be-prod -p "{\\\"spec\\\":{\\\"template\\\":{\\\"metadata\\\":{\\\"labels\\\":{\\\"date\\\":\\\"`date +'%s'`\\\"}}}}}" -n oms
              kubectl rollout status deployment oms-dotnet-be-prod -n oms
              """
            }
          }
          currentBuild.result = 'SUCCESS'

          } else if (env.BRANCH_NAME == 'dev') {
          echo "Process build for ${env.BRANCH_NAME} branch"
          stage('Build docker') {
            container('awscli') {
              sh 'aws s3 cp s3://smartlog-build-config/swm/oms/appsettings.Dev.json src/OMS.WebApi/appsettings.Dev.json'
              DOCKER_LOGIN = sh (
                script: 'aws ecr get-login --no-include-email --region ap-southeast-1',
                returnStdout: true
              ).trim()
            }
            container('docker') {
              REGISTRY_URL="744004065806.dkr.ecr.ap-southeast-1.amazonaws.com/oms-dotnet-be:dev"
              sh """
              ls -la
              docker build -f Dockerfile.dev --network=host -t ${REGISTRY_URL} --pull=true .
              ${DOCKER_LOGIN}
              docker push ${REGISTRY_URL}
              """
            }
          }
          stage('Deploy to k8s') {
            container('kubectl') {
              sh """
              kubectl apply -f deploy.dev.yml
              kubectl patch deployment oms-dotnet-be-dev -p "{\\\"spec\\\":{\\\"template\\\":{\\\"metadata\\\":{\\\"labels\\\":{\\\"date\\\":\\\"`date +'%s'`\\\"}}}}}" -n oms
              kubectl rollout status deployment oms-dotnet-be-dev -n oms
              """
            }
          }
          currentBuild.result = 'SUCCESS'
        } else {
          echo "Khong phai branch (dev|production) nen ko lam gi ca"
          error('Failed to build')
        }
      } catch (exc) {
        currentBuild.result = 'FAILURE'
        echo 'I failed'
        echo exc.getMessage()
      }
      finally {
        echo 'One way or another, I have finished'
        deleteDir() /* clean up our workspace */
        if (currentBuild.result == 'SUCCESS') {
          echo 'Build successful'
          slackSend channel: 'oms-deployment-notification',
            color: 'good',
            message: "Deploy ${currentBuild.fullDisplayName} [${commitMessage}].\nNgonnnnn! :pika_tang:"
        } else if (currentBuild.result == 'FAILURE') {
          echo 'I failed :('
          slackSend channel: 'oms-deployment-notification',
            color: '#c40e0e',
            message: "Deploy ${currentBuild.fullDisplayName} [${commitMessage}].\nKá»³ váº­y ta :pika_think2: :pika_think2: :pika_think2:.\n@here ${env.BUILD_URL}console"
        }
      }
    }
  }
