execution.script:
podTemplate(label: 'common-pod', containers: [
    containerTemplate(name: 'docker', image: 'public.ecr.aws/smartlog/docker:19.03.8', command: 'cat', ttyEnabled: true),
    containerTemplate(name: 'kubectl', image: 'public.ecr.aws/smartlog/roffe/kubectl:v1.13.2', command: 'cat', ttyEnabled: true),
    containerTemplate(name: 'awscli', image: 'public.ecr.aws/smartlog/atlassian/pipelines-awscli:1.18.190', command: 'cat', ttyEnabled: true),
  ],
  volumes: [
    hostPathVolume(mountPath: '/var/run/docker.sock', hostPath: '/var/run/docker.sock'),
  ]) {
    node('common-pod') {
      commitMessage = ""
      try {
        stage('Get source code') {
          echo 'Getting source code...'
          checkout scm

          String[] parts = sh(returnStdout: true, script: 'git log -1 --pretty=%B').trim().split('\n')
          commitMessage = parts[0]
        }
        if (env.BRANCH_NAME == 'develop-test') {
          echo "Process build for ${env.BRANCH_NAME} branch"
          stage('Build docker') {
            container('awscli') {
              DOCKER_LOGIN = sh (
                script: 'aws ecr get-login --no-include-email --region ap-southeast-1',
                returnStdout: true
              ).trim()
            }
            container('docker') {
              REGISTRY_URL="744004065806.dkr.ecr.ap-southeast-1.amazonaws.com/dev-optimization"
              sh """
              ls -la
              docker build --network=host -t ${REGISTRY_URL}:developtest --pull=true .
              ${DOCKER_LOGIN}
              docker push ${REGISTRY_URL}:developtest
              """
            }
          }
          stage('Deploy to k8s') {
            container('kubectl') {
              sh """
              kubectl apply -f deploy.stage.yml
              kubectl patch deployment optimization-developtest -p "{\\\"spec\\\":{\\\"template\\\":{\\\"metadata\\\":{\\\"labels\\\":{\\\"date\\\":\\\"`date +'%s'`\\\"}}}}}" -n common-system
              kubectl rollout status deployment optimization-developtest -n common-system
              """
            }
          }
          currentBuild.result = 'SUCCESS'
        } else
        if (env.BRANCH_NAME == 'master') {
          echo "Process build for ${env.BRANCH_NAME} branch"
          stage('Build docker') {
            container('awscli') {
              DOCKER_LOGIN = sh (
                script: 'aws ecr get-login --no-include-email --region ap-southeast-1',
                returnStdout: true
              ).trim()
            }
            container('docker') {
              REGISTRY_URL="744004065806.dkr.ecr.ap-southeast-1.amazonaws.com/dev-optimization:master"
              sh """
              ls -la
              docker build --network=host -t ${REGISTRY_URL} --pull=true .
              ${DOCKER_LOGIN}
              docker push ${REGISTRY_URL}
              """
            }
          }
          stage('Deploy to k8s') {
            container('kubectl') {
              sh """
              kubectl apply -f deploy.master.yml
              kubectl patch deployment optimization-master -p "{\\\"spec\\\":{\\\"template\\\":{\\\"metadata\\\":{\\\"labels\\\":{\\\"date\\\":\\\"`date +'%s'`\\\"}}}}}" -n common-system
              kubectl rollout status deployment optimization-master -n common-system
              """
            }
          }
          currentBuild.result = 'SUCCESS'
        } else {
          echo "Khong phai branch (develop-test|master) nen ko lam gi ca"
          error('Failed to build')
        }
      } catch (exc) {
        currentBuild.result = 'FAILURE'
        echo 'I failed'
        echo exc.getMessage()
      }
      finally {
        echo 'One way or another, I have finished'
        deleteDir() /* clean up our workspace */
        if (currentBuild.result == 'SUCCESS') {
          echo 'Build successful'
          slackSend channel: 'opt_build_log',
            color: 'good',
            message: "The pipeline ${currentBuild.fullDisplayName} [${commitMessage}] completed successfully."
        } else if (currentBuild.result == 'FAILURE') {
          echo 'I failed :('
          slackSend channel: 'opt_build_log',
            color: '#c40e0e',
            message: "Attention @here ${env.JOB_NAME} #${env.BUILD_NUMBER} [${commitMessage}] has failed."
        }
      }
    }
  }
