execution.script:
podTemplate(label: 'pod-with-gitversion', containers: [
    containerTemplate(name: 'docker', image: 'public.ecr.aws/smartlog/docker:19.03.8', ttyEnabled: true, command: 'cat'),
    containerTemplate(name: 'kubectl', image: 'public.ecr.aws/smartlog/roffe/kubectl:v1.13.2', command: 'cat', ttyEnabled: true),
    containerTemplate(name: 'awscli', image: 'public.ecr.aws/smartlog/atlassian/pipelines-awscli:1.18.190', command: 'cat', ttyEnabled: true),
    containerTemplate(name: 'gitversion', image: 'public.ecr.aws/smartlog/gittools/gitversion:5.3.5-linux-alpine.3.10-x64-netcoreapp3.1', command: 'cat', ttyEnabled: true),
  ],
  volumes: [
    hostPathVolume(mountPath: '/var/run/docker.sock', hostPath: '/var/run/docker.sock'),
  ]) {
    node('pod-with-gitversion') {
      isDeploy = true
      try {
        SEM_VER = '0.0.0'
        stage('Get source code') {
          echo 'Getting source code...'
          checkout([
            $class: 'GitSCM',
            branches: [[name: '*/dev']],
            extensions: scm.extensions,
            userRemoteConfigs: [[
              credentialsId: scm.userRemoteConfigs[0].credentialsId,
              name: 'origin',
              refspec: '+refs/heads/*:refs/remotes/origin/*',
              url: scm.userRemoteConfigs[0].url
            ]],
            doGenerateSubmoduleConfigurations: false
          ])
          checkout scm
          result = sh (
            script: "git log -1 --pretty=%B | grep 'build-all'",
            returnStatus: true
          )
          if (result != 0){
            result = sh (
              script: 'git diff --name-status HEAD~1..HEAD | grep src/Gateway/Smartlog.AppGateway',
              returnStatus: true
            )
          }
          echo "result: ${result}"
        }
        if (env.BRANCH_NAME == 'dev' && result == 0) {
          echo "Process build for ${env.BRANCH_NAME} branch"
          stage('Build docker') {
            container('gitversion') {
              SEM_VER = sh (
                script: '/tools/dotnet-gitversion . /showvariable SemVer',
                returnStdout: true
              ).trim()
              echo "SemVer: ${SEM_VER}"
            }

            container('awscli') {
              sh 'aws s3 cp s3://smartlog-build-config/stx/smartlog-ecosystem-auth/appsettings-app-gateway.Staging.json src/Gateway/Smartlog.AppGateway/appsettings.Production.json'
              sh 'ls -la src/Gateway/Smartlog.AppGateway'
              DOCKER_LOGIN = sh (
                script: 'aws ecr get-login --no-include-email --region ap-southeast-1',
                returnStdout: true
              ).trim()
            }

            container('docker') {
              REGISTRY_URL="744004065806.dkr.ecr.ap-southeast-1.amazonaws.com/staging-smartlog-eco-auth-be:app-gateway"
              sh """
              ls -la
              docker build -f src/Gateway/Smartlog.AppGateway/Dockerfile.staging  --build-arg version=${SEM_VER} --network=host -t ${REGISTRY_URL}-${SEM_VER} -t ${REGISTRY_URL}-latest --pull=true .
              ${DOCKER_LOGIN}
              docker push ${REGISTRY_URL}-${SEM_VER}
              docker push ${REGISTRY_URL}-latest
              """
            }
          }
          stage('Deploy to k8s') {
            container('kubectl') {
              sh """
              kubectl apply -f src/Gateway/Smartlog.AppGateway/deploy.staging.yml
              kubectl patch deployment sml-eco-app-gateway-be-dev -p "{\\\"spec\\\":{\\\"template\\\":{\\\"metadata\\\":{\\\"labels\\\":{\\\"date\\\":\\\"`date +'%s'`\\\"}}}}}" -n eco-system
              kubectl rollout status deployment sml-eco-app-gateway-be-dev -n eco-system
              """
            }
          }
          currentBuild.result = 'SUCCESS'
        } else if (env.BRANCH_NAME == 'dev'  && result != 0) {
          isDeploy = false
          echo 'NO BUILD'
        } else {
          currentBuild.result = 'NO BUILD'
          echo "Khong phai branch dev nen ko lam gi ca"
          error('Failed to build')
        }
      } catch (exc) {
        currentBuild.result = 'FAILURE'
        echo 'I failed'
        echo exc.getMessage()
      }
      finally {
        echo 'One way or another, I have finished'
        try {
          deleteDir() /* clean up our workspace */
        } catch(err) {
          // Do nothing
        }
        if (isDeploy != true) {
          /* Do nothing */
        } else if (currentBuild.result == 'SUCCESS') {
          echo 'Build successful'
          slackSend channel: 'cma-no-boss',
            color: 'good',
            message: "The pipeline ${currentBuild.fullDisplayName} v${SEM_VER} completed successfully."
        } else if (currentBuild.result == 'FAILURE') {
          echo 'I failed :('
          slackSend channel: 'cma-no-boss',
            color: '#c40e0e',
            message: "Attention @here ${env.JOB_NAME} v${SEM_VER} #${env.BUILD_NUMBER} has failed."
        }
      }
    }
  }
