execution.script:
podTemplate(label: 'pod-with-gitversion', containers: [
    containerTemplate(name: 'docker', image: 'public.ecr.aws/smartlog/docker:19.03.8', ttyEnabled: true, command: 'cat'),
    containerTemplate(name: 'kubectl', image: 'public.ecr.aws/smartlog/roffe/kubectl:v1.13.2', command: 'cat', ttyEnabled: true),
    containerTemplate(name: 'awscli', image: 'public.ecr.aws/smartlog/atlassian/pipelines-awscli:1.18.190', command: 'cat', ttyEnabled: true),
    containerTemplate(name: 'gitversion', image: 'public.ecr.aws/smartlog/gittools/gitversion:5.3.5-linux-alpine.3.10-x64-netcoreapp3.1', command: 'cat', ttyEnabled: true),
  ],
  volumes: [
    hostPathVolume(mountPath: '/var/run/docker.sock', hostPath: '/var/run/docker.sock'),
  ]) {
  node('pod-with-gitversion') {
    commitMessage = ""
    try {
      SEM_VER = '0.0.0'
      stage('Get source code') {
        echo 'Getting source code...'
        checkout([
          $class: 'GitSCM',
          branches: [[name: '*/dev']],
          extensions: scm.extensions,
          userRemoteConfigs: [[
            credentialsId: scm.userRemoteConfigs[0].credentialsId,
            name: 'origin',
            refspec: '+refs/heads/*:refs/remotes/origin/*',
            url: scm.userRemoteConfigs[0].url
          ]],
          doGenerateSubmoduleConfigurations: false
        ])
        checkout scm

        String[] parts = sh(returnStdout: true, script: 'git log -1 --pretty=%B').trim().split('\n')
        commitMessage = parts[0]
      }
      if (env.BRANCH_NAME == 'dev') {
        echo "Process build for ${env.BRANCH_NAME} branch"
        REPO="eco-stx-fe"
        TAG="stage"
        DOCKERFILE = "Dockerfile"
        PROJECT = "stx"
        build()
        currentBuild.result = 'SUCCESS'
      } else if (env.BRANCH_NAME == 'production') {
        echo "Process build for ${env.BRANCH_NAME} branch"
        REPO="eco-stx-fe"
        TAG="prod"
        DOCKERFILE = "Dockerfile"
        PROJECT = "stx"
        build()
        currentBuild.result = 'SUCCESS'
      } else if (env.BRANCH_NAME == 'dev_new') {
        echo "Process build for ${env.BRANCH_NAME} branch"
        REPO="eco-stx-fe"
        TAG="dev_new"
        DOCKERFILE = "Dockerfile"
        build()
        currentBuild.result = 'SUCCESS'
      } else {
        echo "Khong phai branch (dev|production) nen ko lam gi ca"
        error('Failed to build')
      }
    } catch (exc) {
      currentBuild.result = 'FAILURE'
      echo 'I failed'
      echo exc.getMessage()
    }
    finally {
      echo 'One way or another, I have finished'
      try {
        deleteDir() /* clean up our workspace */
      } catch(err) {
        // Do nothing
      }
      if (currentBuild.result == 'SUCCESS') {
        echo 'Build successful'
        slackSend channel: 'cma-no-boss',
          color: 'good',
          message: "The pipeline ${currentBuild.fullDisplayName} v${SEM_VER} [${commitMessage}] completed successfully."
      } else if (currentBuild.result == 'FAILURE') {
        echo 'I failed :('
        slackSend channel: 'cma-no-boss',
          color: '#c40e0e',
          message: "Attention @here ${env.JOB_NAME} v${SEM_VER} #${env.BUILD_NUMBER} [${commitMessage}] has failed."
      }
    }
  }
}

def build() {
  stage('Build docker') {
    container('gitversion') {
      SEM_VER = sh (
        script: '/tools/dotnet-gitversion . /showvariable SemVer',
        returnStdout: true
      ).trim()
      echo "SemVer: ${SEM_VER}"
    }
    container('awscli') {
      DOCKER_LOGIN = sh (
        script: 'aws ecr get-login --no-include-email --region ap-southeast-1',
        returnStdout: true
      ).trim()
    }
    container('docker') {
      REGISTRY_URL="744004065806.dkr.ecr.ap-southeast-1.amazonaws.com/eco-stx-fe:${shortEnv}"
      sh """
      ls -la
      docker build -f Dockerfile --build-arg version=${SEM_VER} --network=host -t ${REGISTRY_URL} --pull=true .
      ${DOCKER_LOGIN}
      docker push ${REGISTRY_URL}
      """
    }
  }
  stage('Update GIT') {
    script {
      withCredentials([usernamePassword(credentialsId: 'cicd-bitbucket', passwordVariable: 'BITBUCKET_PASSWORD', usernameVariable: 'BITBUCKET_USERNAME')]) {
        //def encodedPassword = URLEncoder.encode("$GIT_PASSWORD",'UTF-8')
        sh """
        git config --global user.email jenkins@gosmartlog.com
        git config --global user.name jenkins
        git clone https://${BITBUCKET_USERNAME}:${BITBUCKET_PASSWORD}@bitbucket.org/smartlogtms/devops-deployment-${PROJECT}.git
        cd devops-deployment-${PROJECT}
        sed -i 's+${REGISTRY_URL}.*+${REGISTRY_URL}@${SHA256}+g' ./smartlog-transport-exchange-ui/${env.BRANCH_NAME}/.argocd-source.yaml
        git add ./smartlog-transport-exchange-ui/${env.BRANCH_NAME}/.argocd-source.yaml
        for file in ./smartlog-transport-exchange-ui/${env.BRANCH_NAME}/deploy*.yaml; do sed -i "s/\\(date:\\s*\\)'[^']*'/\\1'${currentTimestamp}'/" "\$file";git add "\$file";done
        git commit -m 'Done by Jenkins Job changemanifest: ${REPO}:${env.BUILD_NUMBER}'
        git pull --rebase https://${BITBUCKET_USERNAME}:${BITBUCKET_PASSWORD}@bitbucket.org/smartlogtms/devops-deployment-${PROJECT}.git
        git push https://${BITBUCKET_USERNAME}:${BITBUCKET_PASSWORD}@bitbucket.org/smartlogtms/devops-deployment-${PROJECT}.git HEAD:refs/heads/master
        """
      }
    }
  }
}
