execution.script:
podTemplate(label: 'common-docker20dotnet6', containers: [
    containerTemplate(name: 'kubectl', image: 'public.ecr.aws/smartlog/roffe/kubectl:v1.13.2', command: 'cat', ttyEnabled: true, resourceLimitEphemeralStorage: '100Mi'),
    containerTemplate(name: 'buildkit', image: 'moby/buildkit:v0.10.6', ttyEnabled: true, privileged: true),
    containerTemplate(name: 'dotnet', image: 'danghung/jenkins:all-in-one2', command: 'cat', ttyEnabled: true, resourceLimitEphemeralStorage: '5Gi'),
    containerTemplate(name: 'awscli', image: 'public.ecr.aws/smartlog/atlassian/pipelines-awscli:1.18.190', command: 'cat', ttyEnabled: true,resourceLimitEphemeralStorage: '100Mi'),
  ],
  volumes: [
    hostPathVolume(mountPath: '/var/run/docker.sock', hostPath: '/var/run/docker.sock'),
    configMapVolume(configMapName: 'docker-config', mountPath: '/root/.docker'),   
  ]) {
    node('common-docker20dotnet6') {
      SLACK_CHANNEL = 'C0223PGJMK6' // Default smartlog-stm-v2_5-build-log
      isDeploy = true
      isBuildFe = false
      isBuildBe = false
      isBuildApiGateway = false
      isBuildReport = false

      successBuild = 0
      totalBuild = 0

      commitMessage = ""
      try {
        SEM_VER = '0.0.0'
        stage('Get source code') {
          echo 'Getting source code...'
          checkout scm

          String[] parts = sh(returnStdout: true, script: 'git log -1 --pretty=%B').trim().split('\n')
          commitMessage = parts[0]
          // check last commit has "build-all" string, do build all ( fe, be, api-gateway)
          result = sh (
            script: "git log -1 --pretty=%B | grep 'build-all'",
            returnStatus: true
          )
          isBuildFe = (result == 0)
          isBuildBe = (result == 0)
          isBuildApiGateway = (result == 0)
          isBuildReport = (result == 0)

          if (!isBuildFe){
            // check modified files in last commit, if file path contains "Smartlog.Stm.Web", do build
            isBuildFe = (sh (
              script: "git diff --name-status HEAD~1..HEAD | grep Smartlog.Stm.Web",
              returnStatus: true
            ) == 0)
          }
          echo "isBuildFe: ${isBuildFe}"

          if (!isBuildBe){
            isBuildBe = (sh (
              script: 'git diff --name-status HEAD~1..HEAD | grep -v Jenkinsfile | grep -v Smartlog.Stm.Web | grep -v Smartlog.Stm.ApiGateway',
              returnStatus: true
            ) == 0)
          }
          echo "isBuildBe: ${isBuildBe}"

          if (!isBuildReport){
            isBuildReport = (sh (
              script: 'git diff --name-status HEAD~1..HEAD | grep -v Jenkinsfile | grep -v Smartlog.Stm.Web | grep -v Smartlog.Stm.ApiGateway',
              returnStatus: true
            ) == 0)
          }
          echo "isBuildReport: ${isBuildReport}"

          if (!isBuildApiGateway){
            isBuildApiGateway = (sh (
              script: 'git diff --name-status HEAD~1..HEAD | grep Smartlog.Stm.ApiGateway',
              returnStatus: true
            ) == 0)
          }
          echo "isBuildApiGateway: ${isBuildApiGateway}"

          isDeploy = isBuildFe || isBuildBe || isBuildApiGateway || isBuildReport
          if (isBuildReport) { totalBuild++ }
          if (isBuildFe) { totalBuild++ }
          if (isBuildBe) { totalBuild++ }
          if (isBuildApiGateway) { totalBuild++ }
        }

        rootDir = pwd()
        baseDeploy = load "${rootDir}/Jenkins.Base.groovy"
        deploy = load "${rootDir}/Jenkins.Build.groovy"
        deploy.init(baseDeploy)
        if (env.BRANCH_NAME == 'argocd' && isDeploy) {
          tagEnv = 'dev'
          longEnv = 'Staging'
          build()
        } else if (env.BRANCH_NAME == 'staging' && isDeploy) {
          tagEnv = 'stage'
          longEnv = 'Staging'
          build()
        } else if (env.BRANCH_NAME == 'master' && isDeploy) {
          tagEnv = 'prod'
          longEnv = 'Production'
          build()
        }
      } catch (exc) {
        currentBuild.result = 'FAILURE'
        echo 'I failed'
        echo exc.getMessage()
      }
      finally {
        echo 'One way or another, I have finished'
        try {
          deleteDir() /* clean up our workspace */
        } catch(err) {
          // Do nothing
        }
        if (isDeploy != true) {
          /* Do nothing */
        } else if (currentBuild.result == 'SUCCESS') {
          echo 'Build successful'
        } else if (currentBuild.result == 'FAILURE') {
          echo 'I failed :('
          // slackSend channel: SLACK_CHANNEL,
          //   color: '#c40e0e',
          //   message: "Deploy ${currentBuild.fullDisplayName} [${commitMessage}].\nKỳ vậy ta :pika_think2: :pika_think2: :pika_think2:.\n@here ${env.BUILD_URL}console"
        }
      }
    }
  }


def build() {
  echo "Process build for ${env.BRANCH_NAME} branch"
  SEM_VER = "2.5.0-${tagEnv}.${env.BUILD_NUMBER}"
  echo "Version ${SEM_VER}"
  if (isBuildReport) {
    slackSend channel: SLACK_CHANNEL,
      color: 'good',
      message: "Start build ${currentBuild.fullDisplayName}[Report] v.${SEM_VER} [${commitMessage}] ${successBuild + 1}/${totalBuild}"
    deploy.buildReport(isBuildReport)
    successBuild++
    slackSend channel: SLACK_CHANNEL,
      color: 'good',
      message: "Complete build ${currentBuild.fullDisplayName}[Report] v.${SEM_VER} [${commitMessage}] ${successBuild}/${totalBuild}.\nNgonnnnn! :pika_tang:"
  } else {
    deploy.buildReport(isBuildReport)
  }

  if (isBuildBe) {
    slackSend channel: SLACK_CHANNEL,
        color: 'good',
        message: "Start build ${currentBuild.fullDisplayName}[Be] v.${SEM_VER} [${commitMessage}] ${successBuild}/${totalBuild}."
    deploy.buildBe(isBuildBe)
    successBuild++
    slackSend channel: SLACK_CHANNEL,
      color: 'good',
      message: "Complete build ${currentBuild.fullDisplayName}[Be] v.${SEM_VER} [${commitMessage}] ${successBuild}/${totalBuild}.\nNgonnnnn! :pika_tang:"
  } else {
    deploy.buildBe(isBuildBe)
  }

  if (isBuildFe) {
    slackSend channel: SLACK_CHANNEL,
      color: 'good',
      message: "Start build ${currentBuild.fullDisplayName}[Fe] v.${SEM_VER} [${commitMessage}] ${successBuild + 1}/${totalBuild}"
    deploy.buildFe(isBuildFe)
    successBuild++
    slackSend channel: SLACK_CHANNEL,
      color: 'good',
      message: "Complete build ${currentBuild.fullDisplayName}[Fe] v.${SEM_VER} [${commitMessage}] ${successBuild}/${totalBuild}.\nNgonnnnn! :pika_tang:"
  } else {
    deploy.buildFe(isBuildFe)
  }

  if (isBuildApiGateway) {
    slackSend channel: SLACK_CHANNEL,
      color: 'good',
      message: "Start build ${currentBuild.fullDisplayName}[ApiGateway] v.${SEM_VER} [${commitMessage}] ${successBuild + 1}/${totalBuild}"
    deploy.buildApiGateway(isBuildApiGateway)
    successBuild++
    slackSend channel: SLACK_CHANNEL,
      color: 'good',
      message: "Complete build ${currentBuild.fullDisplayName}[ApiGateway] v.${SEM_VER} [${commitMessage}] ${successBuild}/${totalBuild}.\nNgonnnnn! :pika_tang:"
  } else {
    deploy.buildApiGateway(isBuildApiGateway)
  }

  currentBuild.result = 'SUCCESS'
}

