def generateVersion(running = true) {
    if (running) {
        container('gitversion') {
            SEM_VER = sh (
                script: '/tools/dotnet-gitversion . /showvariable SemVer',
                returnStdout: true
            ).trim()
            echo "SemVer: ${SEM_VER}"
        }
    }
}

def loadS3File(source, dest, running = true) {
    if (running) {
        container('awscli') {
            sh """
                aws s3 cp ${source} ${dest}
            """
        }
    }
}

def removeFile(path, running = true) {
    if (running) {
        container('awscli') {
            sh """
                rm ${path}
            """
        }
    }
}

def git(running = true) {
    if (running) {
        script {
            withCredentials([usernamePassword(credentialsId: 'cicd-bitbucket', passwordVariable: 'BITBUCKET_PASSWORD', usernameVariable: 'BITBUCKET_USERNAME')]) {
              //def encodedPassword = URLEncoder.encode("$GIT_PASSWORD",'UTF-8')

              sh """
              git config --global user.email jenkins@gosmartlog.com
              git config --global user.name jenkins
              rm -rf devops-deployment
              git clone https://${BITBUCKET_USERNAME}:${BITBUCKET_PASSWORD}@bitbucket.org/smartlogtms/devops-deployment.git
              cd devops-deployment
              sed -i 's+${HASH}.*+${HASH}@${SHA256}+g' ./smartlog.stm.2.5/${env.BRANCH_NAME}/${app}/.argocd-source.yaml
              sed -i "s/creationTimestamp:.*/creationTimestamp: '${currentTimestamp}'/" ./smartlog.stm.2.5/${env.BRANCH_NAME}/${app}/.argocd-source.yaml
              git add ./smartlog.stm.2.5/${env.BRANCH_NAME}/${app}/.argocd-source.yaml
              git commit -m 'Done by Jenkins Job changemanifest: stm.25-${app}:${env.BUILD_NUMBER}'
              git push https://${BITBUCKET_USERNAME}:${BITBUCKET_PASSWORD}@bitbucket.org/smartlogtms/devops-deployment.git HEAD:refs/heads/master
              """
            }
        }
    }
}
def buildkit(running = true) {
    if (running) {
        container('dotnet') {
            sh"""
            ls -la ${repoPath}   
            cd ${repoPath}
            dotnet restore
            dotnet publish -c release -o ./app --no-restore  
            """
        } 
        container('buildkit') {
            REGISTRY_URL = """744004065806.dkr.ecr.ap-southeast-1.amazonaws.com/${prefixPod}:argocd"""
            HASH = """744004065806.dkr.ecr.ap-southeast-1.amazonaws.com/${prefixPod}"""
            sh"""
            ls -la ${repoPath}   
            cd ${repoPath}
            wget https://amazon-ecr-credential-helper-releases.s3.us-east-2.amazonaws.com/0.6.0/linux-amd64/docker-credential-ecr-login -O /usr/local/bin/docker-credential-ecr-login
            chmod 755 /usr/local/bin/docker-credential-ecr-login
            buildctl build --frontend dockerfile.v0 --opt platform=linux/amd64,linux/arm64 --local context=. --local dockerfile=./ --opt filename=./Dockerfile.arm.${tagEnv} --output 'type=image,name=${REGISTRY_URL},push=true'
            """
        }
        scr = "aws ecr describe-images --registry-id 744004065806 --repository-name ${prefixPod} --image-ids imageTag=argocd --query 'sort(imageDetails[].imageDigest)' --output text --region ap-southeast-1"
        container('awscli') {
            def sha256 = sh(
              script: scr,
              returnStdout: true
            ).trim()
            SHA256 = sha256
        }
    }
}

