import groovy.transform.Field

@Field private Base = null

def init(base) {
    Base = base
}

def buildFe(running = true) {
    namespace = 'stm'
    repoPath = 'Smartlog.Stm.Web/'
    prefixPod = 'stm-eco-fe'
    prefixPodPublic = 'public-stm-fe'
    app = "fe"
    currentTimestamp = sh(returnStdout: true, script: 'date -u +\'%Y-%m-%dT%H:%M:%SZ\'').trim()
    SHA256 = ""
    stage("Build Fe") {
        Base.loadS3File("s3://smartlog-build-config/stm/config-stm-fe.${tagEnv}.js", "Smartlog.Stm.Web/wwwroot/config.js", running)
        Base.buildkitAndPublic(running)
        Base.git(running)
    }
}
def buildReport(running = true) {
    namespace = 'stm'
    repoPath = 'Smartlog.Stm.Api/'
    prefixPod = 'stm-eco-report'
    prefixPodPublic = 'public-stm-report'
    app = "report"
    currentTimestamp = sh(returnStdout: true, script: 'date -u +\'%Y-%m-%dT%H:%M:%SZ\'').trim()
    SHA256 = ""
    stage("Build Report") {
        Base.loadS3File("s3://smartlog-build-config/stm/appsettings-stm.${tagEnv}.json", "${repoPath}appsettings.${longEnv}.json", running)
        Base.buildkit(running)
        Base.git(running)
    }
}

