execution.script:
podTemplate(label: 'pod-with-gitversion', containers: [
    containerTemplate(name: 'docker', image: 'public.ecr.aws/smartlog/docker:19.03.8', ttyEnabled: true, command: 'cat'),
    containerTemplate(name: 'kubectl', image: 'public.ecr.aws/smartlog/roffe/kubectl:v1.13.2', command: 'cat', ttyEnabled: true),
    containerTemplate(name: 'awscli', image: 'public.ecr.aws/smartlog/atlassian/pipelines-awscli:1.18.190', command: 'cat', ttyEnabled: true),
    containerTemplate(name: 'gitversion', image: 'public.ecr.aws/smartlog/gittools/gitversion:5.3.5-linux-alpine.3.10-x64-netcoreapp3.1', command: 'cat', ttyEnabled: true),
  ],
  volumes: [
    hostPathVolume(mountPath: '/var/run/docker.sock', hostPath: '/var/run/docker.sock'),
  ]) {
  node('pod-with-gitversion') {
    SLACK_CHANNEL = 'C02CR8V040K' // stm_sme_build_log
    isDeploy = true
    isBuildBe = false
    isBuildGPS = false
    isBuildGEO = false
    isBuildApiGateway = false
    // isBuildTenantService = false
    isBuildTaskManager = false
    isBuildTaskManagerService = false
    isBuildSignalR = false
    isBuildLocalService = false

    successBuild = 0
    totalBuild = 0

    commitMessage = ""
    try {
      SEM_VER = '0.0.0'
      stage('Get source code') {
        echo 'Getting source code...'
        checkout([
          $class: 'GitSCM',
          branches: [[name: '*/dev']],
          extensions: scm.extensions,
          userRemoteConfigs: [[
            credentialsId: scm.userRemoteConfigs[0].credentialsId,
            name: 'origin',
            refspec: '+refs/heads/*:refs/remotes/origin/*',
            url: scm.userRemoteConfigs[0].url
          ]],
          doGenerateSubmoduleConfigurations: false
        ])

        checkout scm

        String[] parts = sh(returnStdout: true, script: 'git log -1 --pretty=%B').trim().split('\n')
        commitMessage = parts[0]

        result = sh (
          script: "git log -1 --pretty=%B | grep 'build-all'",
          returnStatus: true
        )
        isBuildBe = (result == 0)
        isBuildGPS = (result == 0)
        isBuildGEO = (result == 0)
        isBuildApiGateway = (result == 0)
        // isBuildTenantService = (result == 0)
        isBuildTaskManager = (result == 0)
        isBuildTaskManagerService = (result == 0)
        isBuildSignalR = (result == 0)
        isBuildLocalService = (result == 0)

        if (!isBuildBe){
          isBuildBe = (sh (
            script: """git diff --name-status HEAD~1..HEAD \\
             | grep -v src/GPS \\
             | grep -v src/Smartlog.Stm.Smes.ApiGateway \\
             | grep -v src/TenantService \\
             | grep -v src/TaskManagementSystem \\
             | grep -v src/SocketService \\
             | grep -v src/Smartlog.Stm.Smes.SignalR \\
             | grep -v src/LocalServices
            """,
            returnStatus: true
          ) == 0)
        }
        echo "isBuildBe: ${isBuildBe}"

        if (!isBuildGPS){
          isBuildGPS = (sh (
            script: 'git diff --name-status HEAD~1..HEAD | grep -E "src/GPS|Jenkins"',
            returnStatus: true
          ) == 0)
        }
        echo "isBuildGPS: ${isBuildGPS}"

        if (!isBuildGEO){
          isBuildGEO = (sh (
            script: 'git diff --name-status HEAD~1..HEAD | grep -E "src/GPS|Jenkins"',
            returnStatus: true
          ) == 0)
        }
        echo "isBuildGEO: ${isBuildGEO}"

        if (!isBuildApiGateway) {
          isBuildApiGateway = (sh (
            script: 'git diff --name-status HEAD~1..HEAD | grep -E "Jenkins|src/Smartlog.Stm.Smes.ApiGateway"',
            returnStatus: true
          ) == 0)
        }
        echo "isBuildApiGateway: ${isBuildApiGateway}"

        // if (!isBuildTenantService) {
        //   isBuildTenantService = (sh (
        //     script: 'git diff --name-status HEAD~1..HEAD | grep src/TenantService',
        //     returnStatus: true
        //   ) == 0)
        // }
        // echo "isBuildTenantService: ${isBuildTenantService}"

        if (!isBuildTaskManager) {
          isBuildTaskManager = (sh (
            script: 'git diff --name-status HEAD~1..HEAD | grep -E "Jenkins|src/TaskManagementSystem"',
            returnStatus: true
          ) == 0)
        }
        echo "isBuildTaskManager: ${isBuildTaskManager}"

        if (!isBuildTaskManagerService) {
          isBuildTaskManagerService = (sh (
            script: 'git diff --name-status HEAD~1..HEAD | grep -E "Jenkins|src/TaskManagementSystem"',
            returnStatus: true
          ) == 0)
        }
        echo "isBuildTaskManagerService: ${isBuildTaskManagerService}"

        if (!isBuildSignalR) {
          isBuildSignalR = (sh (
            script: 'git diff --name-status HEAD~1..HEAD | grep -E "Jenkins|src/Smartlog.Stm.Smes.SignalR"',
            returnStatus: true
          ) == 0)
        }
        echo "isBuildSignalR: ${isBuildSignalR}"

        if (!isBuildLocalService) {
          isBuildLocalService = (sh (
            script: 'git diff --name-status HEAD~1..HEAD | grep -E "Jenkins|src/LocalServices"',
            returnStatus: true
          ) == 0)
        }
        echo "isBuildLocalService: ${isBuildLocalService}"

        // isDeploy = isBuildBe || isBuildGPS || isBuildGEO || isBuildApiGateway || isBuildTenantService || isBuildTaskManager
        // isDeploy = isDeploy || isBuildTaskManagerService || isBuildSignalR || isBuildLocalService

        isDeploy =  isBuildGEO || isBuildLocalService || isBuildTaskManagerService || isBuildBe || isBuildGPS || isBuildApiGateway || isBuildTaskManager || isBuildSignalR 
        if (isBuildBe) { totalBuild++ }
        if (isBuildGPS) { totalBuild++ }
        if (isBuildGEO) { totalBuild++ }
        if (isBuildApiGateway) { totalBuild++ }
        // if (isBuildTenantService) { totalBuild++ }
        if (isBuildTaskManager) { totalBuild++ }
        if (isBuildTaskManagerService) { totalBuild++ }
        if (isBuildSignalR) { totalBuild++ }
        if (isBuildLocalService) { totalBuild++ }
      }

      rootDir = pwd()
      privateEcr = '744004065806.dkr.ecr.ap-southeast-1.amazonaws.com'
      namespace = 'stmsme'
      baseDeploy = load "${rootDir}/Jenkins.Base.groovy"


      deploy = load "${rootDir}/Jenkinsfile-smartlog.Build.groovy"
      deploy.init(baseDeploy)
      baseDeploy.awscli()

      if (env.BRANCH_NAME == 'dev' && isDeploy) {
        tagEnv = 'dev'
        longEnv = 'Development'
        account_id = '744004065806'
        deployDeliveree = true
        PROJECT = "stm-sme"
        build()
      } else if (env.BRANCH_NAME == 'production-sme' && isDeploy) {
        tagEnv = 'prod'
        longEnv = 'Production'
        account_id = '744004065806'
        deployDeliveree = false  
        PROJECT = "stm-sme"
        build()
      } else if (env.BRANCH_NAME == 'qc' && isDeploy) {
        tagEnv = 'qc'
        longEnv = 'Staging'
        account_id = '744004065806'
        deployDeliveree = true
        PROJECT = "stm-sme"
        build()
      }
      currentBuild.result = 'SUCCESS'
    } catch (exc) {
      currentBuild.result = 'FAILURE'
      echo 'I failed'
      echo exc.getMessage()
    }
    finally {
      echo 'One way or another, I have finished'
      try {
        deleteDir() /* clean up our workspace */
      } catch(err) {
        // Do nothing
      }
      if (currentBuild.result == 'SUCCESS') {
        echo 'Build successful'
      } else if (currentBuild.result == 'FAILURE') {
        echo 'I failed :('
        slackSend channel: SLACK_CHANNEL,
          color: '#c40e0e',
          message: "Attention @here ${env.JOB_NAME} #${env.BUILD_NUMBER} [${commitMessage}] has failed."
      }
    }
  }
}

def build() {
  echo "Process build for ${env.BRANCH_NAME} branch"
  baseDeploy.generateVersion()
  echo "Version ${SEM_VER}"

  if (isBuildBe) {
    slackSend channel: SLACK_CHANNEL,
        color: 'good',
        message: "Start build ${currentBuild.fullDisplayName}[stm_sme-Be] v.${SEM_VER} [${commitMessage}] ${successBuild + 1}/${totalBuild}"
    deploy.buildBe(isBuildBe)
    successBuild++
    slackSend channel: SLACK_CHANNEL,
        color: 'good',
        message: "The pipeline ${currentBuild.fullDisplayName}[stm_sme-Be] v.${SEM_VER} [${commitMessage}] ${successBuild}/${totalBuild} completed successfully."
  } else {
    deploy.buildBe(isBuildBe)
  }

    if (isBuildApiGateway) {
    slackSend channel: SLACK_CHANNEL,
        color: 'good',
        message: "Start build ${currentBuild.fullDisplayName}[stm_sme-ApiGateway] v.${SEM_VER} [${commitMessage}] ${successBuild + 1}/${totalBuild}"
    deploy.buildApiGateway(isBuildApiGateway)
    successBuild++
    slackSend channel: SLACK_CHANNEL,
        color: 'good',
        message: "The pipeline ${currentBuild.fullDisplayName}[stm_sme-ApiGateway] v.${SEM_VER} [${commitMessage}] ${successBuild}/${totalBuild} completed successfully."
  } else {
    deploy.buildApiGateway(isBuildApiGateway)
  }

  if (isBuildGPS) {
    slackSend channel: SLACK_CHANNEL,
        color: 'good',
        message: "Start build ${currentBuild.fullDisplayName}[stm_sme-GPS] v.${SEM_VER} [${commitMessage}] ${successBuild + 1}/${totalBuild}"
    deploy.buildGPS(isBuildGPS)
    successBuild++
    slackSend channel: SLACK_CHANNEL,
        color: 'good',
        message: "The pipeline ${currentBuild.fullDisplayName}[stm_sme-GPS] v.${SEM_VER} [${commitMessage}] ${successBuild}/${totalBuild} completed successfully."
  } else {
    deploy.buildGPS(isBuildGPS)
  }

  if (isBuildGEO) {
    slackSend channel: SLACK_CHANNEL,
        color: 'good',
        message: "Start build ${currentBuild.fullDisplayName}[stm_sme-GEO] v.${SEM_VER} [${commitMessage}] ${successBuild + 1}/${totalBuild}"
    deploy.buildGEO(isBuildGEO)
    successBuild++
    slackSend channel: SLACK_CHANNEL,
        color: 'good',
        message: "The pipeline ${currentBuild.fullDisplayName}[stm_sme-GEO] v.${SEM_VER} [${commitMessage}] ${successBuild}/${totalBuild} completed successfully."
  } else {
    deploy.buildGEO(isBuildGEO)
  }

  // if (isBuildTenantService) {
  //   slackSend channel: SLACK_CHANNEL,
  //       color: 'good',
  //       message: "Start build ${currentBuild.fullDisplayName}[stm_sme-TenantService] v.${SEM_VER} [${commitMessage}] ${successBuild + 1}/${totalBuild}"
  //   deploy.buildTenantService(isBuildTenantService)
  //   successBuild++
  //   slackSend channel: SLACK_CHANNEL,
  //       color: 'good',
  //       message: "The pipeline ${currentBuild.fullDisplayName}[stm_sme-TenantService] v.${SEM_VER} [${commitMessage}] ${successBuild}/${totalBuild} completed successfully."
  // } else {
  //   deploy.buildTenantService(isBuildTenantService)
  // }

  if (isBuildTaskManager) {
    slackSend channel: SLACK_CHANNEL,
        color: 'good',
        message: "Start build ${currentBuild.fullDisplayName}[stm_sme-TaskManager] v.${SEM_VER} [${commitMessage}] ${successBuild + 1}/${totalBuild}"
    deploy.buildTaskManager(isBuildTaskManager)
    successBuild++
    slackSend channel: SLACK_CHANNEL,
        color: 'good',
        message: "The pipeline ${currentBuild.fullDisplayName}[stm_sme-TaskManager] v.${SEM_VER} [${commitMessage}] ${successBuild}/${totalBuild} completed successfully."
  } else {
    deploy.buildTaskManager(isBuildTaskManager)
  }

  if (isBuildTaskManagerService) {
    slackSend channel: SLACK_CHANNEL,
        color: 'good',
        message: "Start build ${currentBuild.fullDisplayName}[stm_sme-TaskManagerService] v.${SEM_VER} [${commitMessage}] ${successBuild + 1}/${totalBuild}"
    deploy.buildTaskManagerService(isBuildTaskManagerService)
    successBuild++
    slackSend channel: SLACK_CHANNEL,
        color: 'good',
        message: "The pipeline ${currentBuild.fullDisplayName}[stm_sme-TaskManagerService] v.${SEM_VER} [${commitMessage}] ${successBuild}/${totalBuild} completed successfully."
  } else {
    deploy.buildTaskManagerService(isBuildTaskManagerService)
  }

  if (isBuildSignalR) {
    slackSend channel: SLACK_CHANNEL,
        color: 'good',
        message: "Start build ${currentBuild.fullDisplayName}[stm_sme-SignalR] v.${SEM_VER} [${commitMessage}] ${successBuild + 1}/${totalBuild}"
    deploy.buildSignalR(isBuildSignalR)
    successBuild++
    slackSend channel: SLACK_CHANNEL,
        color: 'good',
        message: "The pipeline ${currentBuild.fullDisplayName}[stm_sme-SignalR] v.${SEM_VER} [${commitMessage}] ${successBuild}/${totalBuild} completed successfully."
  } else {
    deploy.buildSignalR(isBuildSignalR)
  }

  if (isBuildLocalService) {
    slackSend channel: SLACK_CHANNEL,
        color: 'good',
        message: "Start build ${currentBuild.fullDisplayName}[stm_sme-LocalService] v.${SEM_VER} [${commitMessage}] ${successBuild + 1}/${totalBuild}"
    deploy.buildLocalService(isBuildLocalService)
    successBuild++
    slackSend channel: SLACK_CHANNEL,
        color: 'good',
        message: "The pipeline ${currentBuild.fullDisplayName}[stm_sme-LocalService] v.${SEM_VER} [${commitMessage}] ${successBuild}/${totalBuild} completed successfully."
  } else {
    deploy.buildLocalService(isBuildLocalService)
  }
  currentBuild.result = 'SUCCESS'
