/* groovylint-disable LineLength */
def awscli(running = true) {
    if (running) {
        container('awscli') {
            DOCKER_LOGIN = sh (
                script: 'aws ecr get-login --no-include-email --region ap-southeast-1',
                returnStdout: true
            ).trim()
        }
    }
}

def generateVersion(running = true) {
    if (running) {
        container('gitversion') {
            SEM_VER = sh (
                script: '/tools/dotnet-gitversion . /showvariable SemVer',
                returnStdout: true
            ).trim()
            echo "SemVer: ${SEM_VER}"
        }
    }
}

def loadS3File(source, dest, running = true) {
    if (running) {
        container('awscli') {
            sh """
                aws s3 cp ${source} ${dest}
            """
        }
    }
}

def buildDocker(running = true) {
    if (running) {
        container('docker') {
            REGISTRY_URL = """${account_id}.dkr.ecr.ap-southeast-1.amazonaws.com/${prefixPod}:${tagEnv}"""
            sh """
                ls -la ${repoPath}
                docker build -f ${repoPath}Dockerfile --build-arg version=${SEM_VER} --network=host -t ${REGISTRY_URL} --pull=true .
                ${DOCKER_LOGIN}
                docker push ${REGISTRY_URL}
            """
        }
        scr = "aws ecr describe-images --registry-id ${account_id} --repository-name ${prefixPod} --image-ids imageTag=${tagEnv} --query 'sort(imageDetails[].imageDigest)' --output text --region ap-southeast-1"
        container('awscli') { 
            def sha256 = sh(
                script: scr,
                returnStdout: true
            ).trim()
            SHA256 = sha256
        }
        currentTimestamp = sh(returnStdout: true, script: 'date +%s').trim()
        REGISTRY_URL="${account_id}.dkr.ecr.ap-southeast-1.amazonaws.com/${prefixPod}"
        retry(3) {
            script {
                withCredentials([usernamePassword(credentialsId: 'cicd-bitbucket', passwordVariable: 'BITBUCKET_PASSWORD', usernameVariable: 'BITBUCKET_USERNAME')]) {
                //def encodedPassword = URLEncoder.encode("$GIT_PASSWORD",'UTF-8')
                sh """
                git config --global user.email jenkins@gosmartlog.com
                git config --global user.name jenkins
                rm -rf devops-deployment-${PROJECT}
                git clone https://${BITBUCKET_USERNAME}:${BITBUCKET_PASSWORD}@bitbucket.org/smartlogtms/devops-deployment-${PROJECT}.git
                cd devops-deployment-${PROJECT}
                sed -i 's+${REGISTRY_URL}.*+${REGISTRY_URL}@${SHA256}+g' ./stm-be-deliveree/${env.BRANCH_NAME}/${gitFolder}/.argocd-source.yaml
                git add ./stm-be-deliveree/${env.BRANCH_NAME}/${gitFolder}/.argocd-source.yaml
                for file in ./stm-be-deliveree/${env.BRANCH_NAME}/${gitFolder}/deploy*.yaml; do sed -i "s/\\(date:\\s*\\)'[^']*'/\\1'${currentTimestamp}'/" "\$file";git add "\$file";done

                """

                if (deployDeliveree) {
                    sh """
                    cd devops-deployment-${PROJECT}
                    sed -i 's+${REGISTRY_URL}.*+${REGISTRY_URL}@${SHA256}+g' ./stm-be-deliveree/${env.BRANCH_NAME}-deliveree/${gitFolder}/.argocd-source.yaml
                    git add ./stm-be-deliveree/${env.BRANCH_NAME}-deliveree/${gitFolder}/.argocd-source.yaml
                    for file in ./stm-be-deliveree/${env.BRANCH_NAME}-deliveree/${gitFolder}/deploy*.yaml; do sed -i "s/\\(date:\\s*\\)'[^']*'/\\1'${currentTimestamp}'/" "\$file";git add "\$file";done
                    """
                }
                sh """
                cd devops-deployment-${PROJECT}
                git commit -m 'Done by Jenkins Job changemanifest: ${prefixPod}:${env.BUILD_NUMBER}'
                git pull --rebase https://${BITBUCKET_USERNAME}:${BITBUCKET_PASSWORD}@bitbucket.org/smartlogtms/devops-deployment-${PROJECT}.git
                git push https://${BITBUCKET_USERNAME}:${BITBUCKET_PASSWORD}@bitbucket.org/smartlogtms/devops-deployment-${PROJECT}.git HEAD:refs/heads/master
                """
                }
            }
        }

    }
}

def deployToK8s(running = true) {
    if (running) {
        container('kubectl') {
            sh """
                kubectl apply -f ${repoPath}deploy.${tagEnv}.yml
                kubectl patch deployment ${prefixPod}-${tagEnv} -p "{\\\"spec\\\":{\\\"template\\\":{\\\"metadata\\\":{\\\"labels\\\":{\\\"date\\\":\\\"`date +'%s'`\\\"}}}}}" -n ${namespace}
                kubectl rollout status deployment ${prefixPod}-${tagEnv} -n ${namespace}
            """
        }
    }
}

def deployToK8sMultipleCloud(running = true) {
    if (running) {
        container('kubectl') {
            sh """
                kubectl patch deployment ${prefixPod}-${tagEnv} -p "{\\\"spec\\\":{\\\"template\\\":{\\\"metadata\\\":{\\\"labels\\\":{\\\"date\\\":\\\"`date +'%s'`\\\"}}}}}" -n ${namespace}
                kubectl rollout status deployment ${prefixPod}-${tagEnv} -n ${namespace}
            """
        }
    }
}
return this

