execution.script:
podTemplate(label: 'common-pod', containers: [
    containerTemplate(name: 'kubectl', image: 'public.ecr.aws/smartlog/roffe/kubectl:v1.13.2', command: 'cat', ttyEnabled: true),
    containerTemplate(name: 'buildkit', image: 'moby/buildkit:v0.10.6', ttyEnabled: true, privileged: true),
    containerTemplate(name: 'awscli', image: 'public.ecr.aws/smartlog/atlassian/pipelines-awscli:1.18.190', command: 'cat', ttyEnabled: true),
  ],
  volumes: [
    configMapVolume(configMapName: 'docker-config', mountPath: '/root/.docker'),   
  ]) {
  node('common-pod') {
    commitMessage = ''
    try {
      SEM_VER = '0.0.0'
      stage('Get source code') {
        echo 'Getting source code...'
        checkout scm

        String[] parts = sh(returnStdout: true, script: 'git log -1 --pretty=%B').trim().split('\n')
        commitMessage = parts[0]
      }
      if (env.BRANCH_NAME == 'master') {
        echo "Process build for ${env.BRANCH_NAME} branch"
          REPO="stm-visualize"
          TAG="prod"
          DOCKERFILE = "Dockerfile"
          build()
        currentBuild.result = 'SUCCESS'
      } else {
        echo 'Khong phai branch nen ko lam gi ca'
        error('Failed to build')
      }
    } catch (exc) {
      currentBuild.result = 'FAILURE'
      echo 'I failed'
      echo exc.getMessage()
    }
    finally {
      echo 'One way or another, I have finished'
      try {
        deleteDir() /* clean up our workspace */
      } catch (err) {
      // Do nothing
      }
      if (currentBuild.result == 'SUCCESS') {
        echo 'Build successful'
        slackSend channel: 'opt_build_log',
          color: 'good',
          message: "The pipeline ${currentBuild.fullDisplayName} v${SEM_VER} [${commitMessage}] completed successfully."
      } else if (currentBuild.result == 'FAILURE') {
        echo 'I failed :('
        slackSend channel: 'opt_build_log',
          color: '#c40e0e',
          message: "Attention @here ${env.JOB_NAME} v${SEM_VER} #${env.BUILD_NUMBER} [${commitMessage}] has failed."
      }
    }
  }
}

def build() {
  currentTimestamp = sh(returnStdout: true, script: 'date +%s').trim()
  scr = "aws ecr describe-images --registry-id 744004065806 --repository-name ${REPO} --image-ids imageTag=${TAG} --query 'sort(imageDetails[].imageDigest)' --output text --region ap-southeast-1"
  stage('Build') {
    container('buildkit') {
      REGISTRY_URL="744004065806.dkr.ecr.ap-southeast-1.amazonaws.com/${REPO}"
      sh """
      wget https://amazon-ecr-credential-helper-releases.s3.us-east-2.amazonaws.com/0.6.0/linux-amd64/docker-credential-ecr-login -O /usr/local/bin/docker-credential-ecr-login
      chmod 755 /usr/local/bin/docker-credential-ecr-login
      buildctl build --frontend dockerfile.v0 --opt platform=linux/amd64,linux/arm64 --local context=. --local dockerfile=./ --opt filename=./${DOCKERFILE} --output 'type=image,name=${REGISTRY_URL}:${TAG},push=true'
      """
    }
    container('awscli') {
      def sha256 = sh(
        script: scr,
        returnStdout: true
      ).trim()
      SHA256 = sha256
    }
  }
  stage('Update GIT') {
    script {
      withCredentials([usernamePassword(credentialsId: 'cicd-bitbucket', passwordVariable: 'BITBUCKET_PASSWORD', usernameVariable: 'BITBUCKET_USERNAME')]) {
        //def encodedPassword = URLEncoder.encode("$GIT_PASSWORD",'UTF-8')
        sh """
        git config --global user.email jenkins@gosmartlog.com
        git config --global user.name jenkins
        git clone https://${BITBUCKET_USERNAME}:${BITBUCKET_PASSWORD}@bitbucket.org/smartlogtms/devops-deployment.git
        cd devops-deployment
        sed -i 's+${REGISTRY_URL}.*+${REGISTRY_URL}@${SHA256}+g' ./visualize-thp/${env.BRANCH_NAME}/.argocd-source.yaml
        git add ./visualize-thp/${env.BRANCH_NAME}/.argocd-source.yaml
        for file in ./visualize-thp/${env.BRANCH_NAME}/deploy*.yaml; do sed -i "s/\\(date:\\s*\\)'[^']*'/\\1'${currentTimestamp}'/" "\$file";git add "\$file";done
        git commit -m 'Done by Jenkins Job changemanifest: ${REPO}:${env.BUILD_NUMBER}'
        git push https://${BITBUCKET_USERNAME}:${BITBUCKET_PASSWORD}@bitbucket.org/smartlogtms/devops-deployment.git HEAD:refs/heads/master
        """
      }
    }
  }
  currentBuild.result = 'SUCCESS'
}
