execution.script:
podTemplate(label: 'common-pod', containers: [
    containerTemplate(name: 'docker', image: 'public.ecr.aws/smartlog/docker:19.03.8', ttyEnabled: true, command: 'cat'),
    containerTemplate(name: 'kubectl', image: 'public.ecr.aws/smartlog/roffe/kubectl:v1.13.2', command: 'cat', ttyEnabled: true),
    containerTemplate(name: 'buildkit', image: 'moby/buildkit:v0.10.6', ttyEnabled: true, privileged: true),
    containerTemplate(name: 'awscli', image: 'public.ecr.aws/smartlog/atlassian/pipelines-awscli:1.18.190', command: 'cat', ttyEnabled: true),
  ],
  volumes: [
    hostPathVolume(mountPath: '/var/run/docker.sock', hostPath: '/var/run/docker.sock'),
    configMapVolume(configMapName: 'docker-config', mountPath: '/root/.docker'),   
  ]) {
  node('common-pod') {
    commitMessage = ''
    authorName = ''
    try {
      SEM_VER = '3.2.3'
      stage('Get source code') {
        echo 'Getting source code...'
        checkout scm
        String[] parts = sh(returnStdout: true, script: 'git log -1 --pretty=%B').trim().split('\n')
        if (parts.length > 2) {
          commitMessage = parts[2]
        } else {
          commitMessage = parts[0]
        }
        String[] glAuthorName = sh(returnStdout: true, script: 'git log -2 --pretty=%an').trim().split('\n')
        authorName = glAuthorName[0]
      }
      if (env.BRANCH_NAME == 'master') {
        registryUrl = '744004065806.dkr.ecr.ap-southeast-1.amazonaws.com/smartlog-optimization:vrp'
        REPO="smartlog-optimization"
        TAG="vrp"
        DOCKERFILE = "Dockerfile"
        PROJECT = "stm2.5"
        build()
      } else if (env.BRANCH_NAME == 'develop_v3') {
        echo "Process build for ${env.BRANCH_NAME} branch"
        REPO="smartlog-optimization"
        TAG="vrp-develop-v3"
        DOCKERFILE = "Dockerfile"
        PROJECT = "stm2.5"
        build()
      } else {
        error('Failed to build')
      }
    } catch (exc) {
      currentBuild.result = 'FAILURE'
      echo exc.getMessage()
    }
    finally {
      try {
        deleteDir()
      } catch (err) {
      // Do nothing
      }
      if (currentBuild.result == 'SUCCESS') {
        slackSend channel: 'opt_build_log',
          color: 'good',
          message: "The pipeline *${currentBuild.fullDisplayName}* (version: ${SEM_VER}) [${commitMessage}] completed successfully.\n _-${authorName}-_ "
      } else if (currentBuild.result == 'FAILURE') {
        slackSend channel: 'opt_build_log',
          color: '#c40e0e',
          message: "Attention @here ${env.JOB_NAME} v${SEM_VER} #${env.BUILD_NUMBER} [${commitMessage}] has failed."
      }
    }
  }
}

def build() {
  currentTimestamp = sh(returnStdout: true, script: 'date +%s').trim()
  scr = "aws ecr describe-images --registry-id 744004065806 --repository-name ${REPO} --image-ids imageTag=${TAG} --query 'sort(imageDetails[].imageDigest)' --output text --region ap-southeast-1"
  stage('Build') {
    container('buildkit') {
      REGISTRY_URL="744004065806.dkr.ecr.ap-southeast-1.amazonaws.com/${REPO}"
      sh """
      wget https://amazon-ecr-credential-helper-releases.s3.us-east-2.amazonaws.com/0.6.0/linux-amd64/docker-credential-ecr-login -O /usr/local/bin/docker-credential-ecr-login
      chmod 755 /usr/local/bin/docker-credential-ecr-login
      buildctl build --frontend dockerfile.v0 --local context=. --local dockerfile=./ --opt filename=./${DOCKERFILE} --output 'type=image,name=${REGISTRY_URL}:${TAG},push=true'
      """
    }
    container('awscli') {
      def sha256 = sh(
        script: scr,
        returnStdout: true
      ).trim()
      SHA256 = sha256
    }
  }
  stage('Update GIT') {
    script {
      withCredentials([usernamePassword(credentialsId: 'cicd-bitbucket', passwordVariable: 'BITBUCKET_PASSWORD', usernameVariable: 'BITBUCKET_USERNAME')]) {
        //def encodedPassword = URLEncoder.encode("$GIT_PASSWORD",'UTF-8')
        sh """
        git config --global user.email jenkins@gosmartlog.com
        git config --global user.name jenkins
        git clone https://${BITBUCKET_USERNAME}:${BITBUCKET_PASSWORD}@bitbucket.org/smartlogtms/devops-deployment-${PROJECT}.git
        cd devops-deployment-${PROJECT}
        sed -i 's+${REGISTRY_URL}.*+${REGISTRY_URL}@${SHA256}+g' ./super-vrp/${env.BRANCH_NAME}/.argocd-source.yaml
        git add ./super-vrp/${env.BRANCH_NAME}/.argocd-source.yaml
        for file in ./super-vrp/${env.BRANCH_NAME}/deploy*.yaml; do sed -i "s/\\(date:\\s*\\)'[^']*'/\\1'${currentTimestamp}'/" "\$file";git add "\$file";done
        """
        if (deployDeliveree) {
          sh"""  
          sed -i 's+${REGISTRY_URL}.*+${REGISTRY_URL}@${SHA256}+g' ./super-vrp/${env.BRANCH_NAME}-deliveree/.argocd-source.yaml
          git add ./super-vrp/${env.BRANCH_NAME}-deliveree/.argocd-source.yaml
          for file in ./super-vrp/${env.BRANCH_NAME}-deliveree/deploy*.yaml; do sed -i "s/\\(date:\\s*\\)'[^']*'/\\1'${currentTimestamp}'/" "\$file";git add "\$file";done
          """
        }
        sh"""
        git commit -m 'Done by Jenkins Job changemanifest: ${REPO}:${env.BUILD_NUMBER}'
        git pull --rebase https://${BITBUCKET_USERNAME}:${BITBUCKET_PASSWORD}@bitbucket.org/smartlogtms/devops-deployment-${PROJECT}.git
        git push https://${BITBUCKET_USERNAME}:${BITBUCKET_PASSWORD}@bitbucket.org/smartlogtms/devops-deployment-${PROJECT}.git HEAD:refs/heads/master
        """
      }
    }
  }
  currentBuild.result = 'SUCCESS'
}
