execution.script:
podTemplate(label: 'common-pod', containers: [
    containerTemplate(name: 'docker', image: 'public.ecr.aws/smartlog/docker:19.03.8', command: 'cat', ttyEnabled: true),
    containerTemplate(name: 'kubectl', image: 'public.ecr.aws/smartlog/roffe/kubectl:v1.13.2', command: 'cat', ttyEnabled: true),
    containerTemplate(name: 'awscli', image: 'public.ecr.aws/smartlog/atlassian/pipelines-awscli:1.18.190', command: 'cat', ttyEnabled: true),
  ],
  volumes: [
    hostPathVolume(mountPath: '/var/run/docker.sock', hostPath: '/var/run/docker.sock'),
  ]) {
    node('common-pod') {
      try {
        stage('Get source code') {
          echo 'Getting source code...'
          checkout scm
        }
        if (env.BRANCH_NAME == 'production') {
          echo "Process build for ${env.BRANCH_NAME} branch"
          stage('Build docker') {
            container('awscli') {
              DOCKER_LOGIN = sh (
                script: 'aws ecr get-login --no-include-email --region ap-southeast-1',
                returnStdout: true
              ).trim()
            }
            container('docker') {
              REGISTRY_URL="744004065806.dkr.ecr.ap-southeast-1.amazonaws.com/prod-swm-auth-fe"
              sh """
              ls -la
              docker build -f Dockerfile.prod --network=host -t ${REGISTRY_URL}:latest --pull=true .
              ${DOCKER_LOGIN}
              docker push ${REGISTRY_URL}:latest
              """
            }
          }
          stage('Deploy to k8s') {
            container('kubectl') {
              sh """
              kubectl apply -f deploy.prod.yml
              kubectl patch deployment swm-auth-fe-prod -p "{\\\"spec\\\":{\\\"template\\\":{\\\"metadata\\\":{\\\"labels\\\":{\\\"date\\\":\\\"`date +'%s'`\\\"}}}}}" -n swm
              kubectl rollout status deployment swm-auth-fe-prod -n swm
              """
            }
          }
          currentBuild.result = 'SUCCESS'
        } else if (env.BRANCH_NAME == 'dev') {
          echo "Process build for ${env.BRANCH_NAME} branch"
          stage('Build docker') {
            container('awscli') {
              DOCKER_LOGIN = sh (
                script: 'aws ecr get-login --no-include-email --region ap-southeast-1',
                returnStdout: true
              ).trim()
            }
            container('docker') {
              REGISTRY_URL="744004065806.dkr.ecr.ap-southeast-1.amazonaws.com/dev-swm-auth-fe"
              sh """
              ls -la
              docker build -f Dockerfile.stage --network=host -t ${REGISTRY_URL}:latest --pull=true .
              ${DOCKER_LOGIN}
              docker push ${REGISTRY_URL}:latest
              """
            }
          }
          stage('Deploy to k8s') {
            container('kubectl') {
              sh """
              kubectl apply -f deploy.stage.yml
              kubectl patch deployment swm-auth-fe-dev -p "{\\\"spec\\\":{\\\"template\\\":{\\\"metadata\\\":{\\\"labels\\\":{\\\"date\\\":\\\"`date +'%s'`\\\"}}}}}" -n swm
              kubectl rollout status deployment swm-auth-fe-dev -n swm
              """
            }
          }
          currentBuild.result = 'SUCCESS'
        } else {
          echo "Khong phai branch (production|dev) nen ko lam gi ca"
          error('Failed to build')
        }
      } catch (exc) {
        currentBuild.result = 'FAILURE'
        echo 'I failed'
        echo exc.getMessage()
      }
      finally {
        echo 'One way or another, I have finished'
        deleteDir() /* clean up our workspace */
        if (currentBuild.result == 'SUCCESS') {
          echo 'Build successful'
          slackSend channel: 'sabeco-system-notification',
            color: 'good',
            message: "The pipeline ${currentBuild.fullDisplayName} completed successfully."
        } else if (currentBuild.result == 'FAILURE') {
          echo 'I failed :('
          slackSend channel: 'sabeco-system-notification',
            color: '#c40e0e',
            message: "Attention @here ${env.JOB_NAME} #${env.BUILD_NUMBER} has failed."
        }
      }
    }
  }
